import React, { useState, useMemo, memo, useEffect } from 'react';
import {
  Table,
  TextInput,
  Paper,
  Group,
  Text,
  Loader,
  Badge,
  ActionIcon,
  Box,
  Select,
  Button,
  Stack
} from '@mantine/core';
import { createColumnHelper, flexRender, getCoreRowModel, useReactTable, getSortedRowModel, getFilteredRowModel, getPaginationRowModel, type SortingState, type ColumnFiltersState } from '@tanstack/react-table';
import { IconSearch, IconRefresh, IconSortAscending, IconSortDescending } from '@tabler/icons-react';
import { PriceCell } from './shared';
import type { Asset, AssetSelectorProps } from '../types/asset';
import { useDebounced } from '../hooks/useDebounced';
import { usePriceChangeAnimation } from '../hooks/usePriceChangeAnimation';
import { useAssets } from '../hooks/useAssets';

const columnHelper = createColumnHelper<Asset>();

const AssetSelector: React.FC<AssetSelectorProps> = ({
  selectedAsset,
  onAssetSelect,
  assets,
  loading = false,
  error = null,
}) => {
  // Stan dla sortowania, filtrowania, paginacji
  const [sorting, setSorting] = useState<SortingState>([]);
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [globalFilter, setGlobalFilter] = useState('');
  const [pagination, setPagination] = useState({
    pageIndex: 0,
    pageSize: 10,
  });
  // Filtr rynku (quote): ALL, USDT, BTC, ETH, BNB
  const [selectedMarket, setSelectedMarket] = useState<'ALL' | 'USDT' | 'BTC' | 'ETH' | 'BNB'>('USDT');

  // Skieruj subskrypcje WS na wybrany rynek (optymalizacja ruchu)
  const { setPreferredQuotes } = useAssets();
  useEffect(() => {
    if (selectedMarket === 'ALL') {
      setPreferredQuotes(null);
    } else {
      setPreferredQuotes([selectedMarket]);
    }
    // po zmianie rynku wr√≥ƒá na pierwszƒÖ stronƒô
    setPagination((prev) => ({ ...prev, pageIndex: 0 }));
  }, [selectedMarket]);

  // Debounced search dla lepszej wydajno≈õci
  const debouncedGlobalFilter = useDebounced(globalFilter, 300);

  // Hook do animacji zmian cen
  // Filtrowanie/grupowanie wg wybranego rynku
  const displayAssets = useMemo(() => {
    if (selectedMarket !== 'ALL') {
      return assets.filter(a => a.quoteAsset === selectedMarket);
    }
    // ALL: wybierz dla ka≈ºdej monety jednƒÖ parƒô wg preferencji quote
    const preference = ['USDT', 'BTC', 'ETH', 'BNB'];
    const byBase = new Map<string, Asset>();
    for (const asset of assets) {
      const current = byBase.get(asset.baseAsset);
      if (!current) {
        byBase.set(asset.baseAsset, asset);
        continue;
      }
      const curRank = preference.indexOf(current.quoteAsset);
      const nextRank = preference.indexOf(asset.quoteAsset);
      const curScore = curRank === -1 ? Number.MAX_SAFE_INTEGER : curRank;
      const nextScore = nextRank === -1 ? Number.MAX_SAFE_INTEGER : nextRank;
      if (nextScore < curScore) byBase.set(asset.baseAsset, asset);
      // je≈õli ten sam priorytet, wybierz wiƒôkszy wolumen
      else if (nextScore === curScore && asset.volume > current.volume) byBase.set(asset.baseAsset, asset);
    }
    return Array.from(byBase.values());
  }, [assets, selectedMarket]);

  // Hook do animacji zmian cen bazujƒÖcy na faktycznie renderowanych wierszach
  const priceChanges = usePriceChangeAnimation(displayAssets);

  // Definicja kolumn dla tabeli
  const columns = useMemo(
    () => [
      columnHelper.accessor('baseAsset', {
        header: 'Krypto',
        cell: (info) => {
          const base = info.getValue();
          const quote = info.row.original.quoteAsset;
          return (
            <Group gap={6} wrap="nowrap">
              <Text fw={600} c="blue">{base}</Text>
              {selectedMarket === 'ALL' && (
                <Badge variant="light" size="xs" color="gray">{quote}</Badge>
              )}
            </Group>
          );
        },
      }),
      columnHelper.accessor('price', {
        header: 'Cena',
        cell: (info) => {
          const symbol = info.row.original.symbol;
          const change = priceChanges.get(symbol);
          const price = info.getValue();

          return (
            <PriceCell 
              price={price}
              change={change || undefined}
            />
          );
        },
      }),
      columnHelper.accessor('priceChangePercent', {
        header: '24h %',
        cell: (info) => {
          const value = info.getValue();
          const isPositive = value >= 0;
          return (
            <Badge
              color={isPositive ? 'teal' : 'red'}
              variant="light"
              size="sm"
            >
              {isPositive ? '+' : ''}{value.toFixed(2)}%
            </Badge>
          );
        },
      }),
      columnHelper.accessor('volume', {
        header: 'Wolumen 24h',
        cell: (info) => {
          const v = info.getValue();
          let display: string;
          if (v === 0) display = '0';
          else if (v >= 1_000_000_000) display = (v / 1_000_000_000).toFixed(2) + 'B';
          else if (v >= 1_000_000) display = (v / 1_000_000).toFixed(2) + 'M';
          else if (v >= 10_000) display = (v / 1_000).toFixed(2) + 'K';
          else if (v >= 1) display = v.toFixed(2);
          else if (v >= 0.01) display = v.toFixed(4); // ma≈Çe ale czytelne
          else display = v.toExponential(2);
          return (
            <Text ta="right" ff="monospace" size="sm">
              {display}
            </Text>
          );
        },
      }),
      columnHelper.display({
        id: 'actions',
        header: 'Akcje',
        cell: (info) => (
          <Button
            size="xs"
            variant={selectedAsset === info.row.original.symbol ? 'filled' : 'outline'}
            onClick={() => onAssetSelect(info.row.original)}
          >
            {selectedAsset === info.row.original.symbol ? 'Wybrano' : 'Wybierz'}
          </Button>
        ),
      }),
    ],
    [selectedAsset, onAssetSelect]
  );

  // Konfiguracja tabeli TanStack
  const table = useReactTable({
    data: displayAssets,
    columns,
    state: {
      sorting,
      columnFilters,
      globalFilter: debouncedGlobalFilter, // Use debounced value
      pagination,
    },
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    onGlobalFilterChange: setGlobalFilter,
    onPaginationChange: setPagination,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    // Wa≈ºne: nie resetuj paginacji przy ka≈ºdej zmianie danych (np. update tickera)
    autoResetPageIndex: false,
  debugTable: (typeof process !== 'undefined' && (process as any).env && (process as any).env.NODE_ENV === 'development') || false,
  });

  // Zabezpieczenie: je≈õli po filtracji/zmianie pageSize obecny pageIndex wykracza poza zakres, zawƒô≈∫ go
  useEffect(() => {
    const pageCount = table.getPageCount();
    if (pageCount === 0 && pagination.pageIndex !== 0) {
      setPagination((prev) => ({ ...prev, pageIndex: 0 }));
      return;
    }
    if (pageCount > 0 && pagination.pageIndex > pageCount - 1) {
      setPagination((prev) => ({ ...prev, pageIndex: pageCount - 1 }));
    }
  }, [displayAssets.length, debouncedGlobalFilter, pagination.pageSize, sorting, table, pagination.pageIndex]);

  if (loading) {
    return (
      <Paper p="md" withBorder>
        <Group justify="center">
          <Loader size="md" />
          <Text>≈Åadowanie aktyw√≥w...</Text>
        </Group>
      </Paper>
    );
  }

  if (error) {
    return (
      <Paper p="md" withBorder>
        <Group justify="center" c="red">
          <Text>B≈ÇƒÖd: {error}</Text>
          <ActionIcon variant="outline" color="red">
            <IconRefresh size={16} />
          </ActionIcon>
        </Group>
      </Paper>
    );
  }

  return (
    <Stack gap="md">
      {/* Sekcja wyszukiwania i filtr√≥w */}
      <Paper p="md" withBorder>
        <Stack gap="sm">
          <Group justify="space-between">
            <Text size="lg" fw={600}>
              Wyb√≥r aktywa
            </Text>
            <Group gap="xs">
              <Text size="sm" c="dimmed">
                Widoczne: {table.getFilteredRowModel().rows.length} / {assets.length} aktyw√≥w {selectedMarket !== 'ALL' ? `(rynek ${selectedMarket})` : ``}
              </Text>
            </Group>
          </Group>

          <Group>
            <TextInput
              placeholder="Szukaj par (np. BTC, ETH...)"
              leftSection={<IconSearch size={16} />}
              value={globalFilter ?? ''}
              onChange={(event) => setGlobalFilter(event.currentTarget.value)}
              style={{ flexGrow: 1 }}
            />
            <Select
              placeholder="Rynek"
              data={[
                { value: 'ALL', label: 'Wszystkie rynki' },
                { value: 'USDT', label: 'Rynek USDT' },
                { value: 'BNB', label: 'Rynki BNB' },
                { value: 'BTC', label: 'Rynki BTC' },
                { value: 'ETH', label: 'Rynki ETH' },
              ]}
              value={selectedMarket}
              onChange={(value) => setSelectedMarket((value as any) ?? 'ALL')}
              w={160}
            />
            <Select
              placeholder="Rozmiar strony"
              data={[
                { value: '5', label: '5' },
                { value: '10', label: '10' },
                { value: '25', label: '25' },
                { value: '50', label: '50' },
              ]}
              value={pagination.pageSize.toString()}
              onChange={(value) =>
                setPagination((prev) => ({
                  ...prev,
                  pageSize: parseInt(value || '10'),
                  pageIndex: 0,
                }))
              }
              w={120}
            />
          </Group>
        </Stack>
      </Paper>

      {/* Tabela */}
      <Paper withBorder>
        <Table striped highlightOnHover>
          <Table.Thead>
            {table.getHeaderGroups().map((headerGroup) => (
              <Table.Tr key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <Table.Th key={header.id}>
                    {header.isPlaceholder ? null : (
                      <Group gap="xs">
                        <Box
                          style={{ cursor: header.column.getCanSort() ? 'pointer' : 'default' }}
                          onClick={header.column.getToggleSortingHandler()}
                        >
                          {flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                        </Box>
                        {header.column.getCanSort() && (
                          <ActionIcon
                            size="xs"
                            variant="transparent"
                            color="dimmed"
                          >
                            {header.column.getIsSorted() === 'asc' ? (
                              <IconSortAscending size={12} />
                            ) : header.column.getIsSorted() === 'desc' ? (
                              <IconSortDescending size={12} />
                            ) : null}
                          </ActionIcon>
                        )}
                      </Group>
                    )}
                  </Table.Th>
                ))}
              </Table.Tr>
            ))}
          </Table.Thead>
          <Table.Tbody>
            {table.getRowModel().rows.map((row) => (
              <Table.Tr
                key={row.id}
                style={{
                  backgroundColor:
                    selectedAsset === row.original.symbol
                      ? 'var(--mantine-color-blue-light)'
                      : undefined,
                }}
              >
                {row.getVisibleCells().map((cell) => (
                  <Table.Td key={cell.id}>
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </Table.Td>
                ))}
              </Table.Tr>
            ))}
          </Table.Tbody>
        </Table>

        {/* Paginacja */}
        <Group justify="space-between" p="md">
          <Text size="sm" c="dimmed">
            Strona {table.getState().pagination.pageIndex + 1} z{' '}
            {table.getPageCount()} (
            {table.getFilteredRowModel().rows.length} aktyw√≥w)
          </Text>
          <Group gap="xs">
            <Button
              size="xs"
              variant="outline"
              disabled={!table.getCanPreviousPage()}
              onClick={() => table.previousPage()}
            >
              Poprzednia
            </Button>
            <Button
              size="xs"
              variant="outline"
              disabled={!table.getCanNextPage()}
              onClick={() => table.nextPage()}
            >
              Nastƒôpna
            </Button>
          </Group>
        </Group>
      </Paper>
    </Stack>
  );
};

export default memo(AssetSelector);

import React, { useState, useEffect } from 'react';
import {
  Paper,
  Stack,
  Group,
  Text,
  Title,
  Button,
  Select,
  NumberInput,
  TextInput,
  Grid,
  Badge,
  Alert,
  Card,
  Accordion,
} from '@mantine/core';
import {
  IconSettings,
  IconRefresh,
  IconCheck,
  IconX,
  IconInfoCircle,
} from '@tabler/icons-react';
import { secureApiCall, API_CONFIG } from '../config/api';

interface StrategyConfig {
  type: string;
  symbol: string;
  timeframe: string;
  parameters: {
    // Common parameters
    period?: number;

    // RSI strategy parameters
    rsi_period?: number;
    rsi_overbought?: number;  // Changed from rsi_upper
    rsi_oversold?: number;    // Changed from rsi_lower

    // Simple MA strategy parameters
    ma_period?: number;       // Changed from sma_period
    ma_type?: string;
    threshold?: number;

    // Grid trading parameters
    grid_levels?: number;
    grid_spacing?: number;
    grid_amount?: number;

    // DCA strategy parameters
    dca_interval?: number;
    dca_amount?: number;
    dca_price_drop?: number;
  };
  risk_management: {
    max_position_size: number;
    stop_loss_percentage: number;
    take_profit_percentage: number;
  };
}

interface BotConfigPanelProps {
  isRunning: boolean;
  onConfigUpdate?: () => void;
}

const BotConfigPanel: React.FC<BotConfigPanelProps> = ({ isRunning, onConfigUpdate }) => {
  const [config, setConfig] = useState<StrategyConfig | null>(null);
  const [strategies, setStrategies] = useState<Array<{value: string, label: string}>>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const loadBotConfig = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await secureApiCall(API_CONFIG.ENDPOINTS.BOT_CONFIG);
      const data = await response.json();
      console.log('Bot config response:', data);
      if (data && data.config) {
        setConfig(data.config);
      }
    } catch (error) {
      console.error('Failed to load bot config:', error);
      setError('Failed to load configuration');
    } finally {
      setLoading(false);
    }
  };

  const loadStrategies = async () => {
    try {
      const response = await secureApiCall(API_CONFIG.ENDPOINTS.BOT_STRATEGIES);
      const data = await response.json();
      console.log('Strategies response:', data);
      if (data && data.strategies) {
        // Przekszta≈Çƒá obiekt strategii na tablicƒô dla Select komponentu
        const strategiesObj = data.strategies;
        const strategiesArray = Object.keys(strategiesObj).map(key => ({
          value: key,
          label: strategiesObj[key].name || key
        }));
        setStrategies(strategiesArray);
      }
    } catch (error) {
      console.error('Failed to load strategies:', error);
      setError('Failed to load strategies');
    }
  };

  const updateConfig = async () => {
    if (!config) return;

    try {
      setLoading(true);
      setError(null);
      setSuccess(false);

      console.log('Sending config update:', config);
      const response = await fetch(API_CONFIG.BASE_URL + API_CONFIG.ENDPOINTS.BOT_CONFIG, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(config),
      });
      const data = await response.json();
      console.log('Config update response:', data);

      setSuccess(true);
      if (onConfigUpdate) {
        onConfigUpdate();
      }

      setTimeout(() => setSuccess(false), 3000);
    } catch (error: any) {
      console.error('Failed to update config:', error);
      setError(error.response?.data?.detail || 'Failed to update configuration');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBotConfig();
    loadStrategies();
  }, []);

  if (!config) {
    return (
      <Paper p="md">
        <Stack>
          <Group>
            <IconSettings size={24} />
            <Title order={3}>Bot Configuration</Title>
          </Group>
          {loading && <Text>Loading...</Text>}
          {error && <Alert color="red" icon={<IconX size={16} />}>{error}</Alert>}
        </Stack>
      </Paper>
    );
  }

  return (
    <Paper p="md">
      <Stack>
        <Group justify="space-between">
          <Group>
            <IconSettings size={24} />
            <Title order={3}>Bot Configuration</Title>
          </Group>
          <Group>
            <Button 
              variant="light" 
              leftSection={<IconRefresh size={16} />}
              onClick={() => { loadBotConfig(); loadStrategies(); }}
              loading={loading}
            >
              Refresh
            </Button>
            <Button 
              onClick={updateConfig}
              loading={loading}
              disabled={isRunning}
              leftSection={<IconCheck size={16} />}
            >
              Save Configuration
            </Button>
          </Group>
        </Group>

        {error && (
          <Alert color="red" icon={<IconX size={16} />}>
            {error}
          </Alert>
        )}

        {success && (
          <Alert color="green" icon={<IconCheck size={16} />}>
            Configuration updated successfully!
          </Alert>
        )}

        {isRunning && (
          <Alert color="orange" icon={<IconInfoCircle size={16} />}>
            Bot is running. Stop the bot to modify configuration.
          </Alert>
        )}

        <Grid>
          <Grid.Col span={6}>
            <Select
              label="Strategy Type"
              placeholder="Select strategy"
              data={strategies}
              value={config.type}
              onChange={(value) => setConfig({ ...config, type: value || '' })}
              disabled={isRunning}
            />
          </Grid.Col>
          <Grid.Col span={6}>
            <TextInput
              label="Symbol"
              placeholder="e.g., BTCUSDT"
              value={config.symbol}
              onChange={(event) => setConfig({ ...config, symbol: event.target.value })}
              disabled={isRunning}
            />
          </Grid.Col>
          <Grid.Col span={6}>
            <Select
              label="Timeframe"
              placeholder="Select timeframe"
              data={['1m', '5m', '15m', '1h', '4h', '1d']}
              value={config.timeframe}
              onChange={(value) => setConfig({ ...config, timeframe: value || '1m' })}
              disabled={isRunning}
            />
          </Grid.Col>
        </Grid>

        <Accordion defaultValue="strategy">
          <Accordion.Item value="strategy">
            <Accordion.Control>Strategy Parameters</Accordion.Control>
            <Accordion.Panel>
              <Grid>
                {config.type === 'simple_ma' && (
                  <>
                    <Grid.Col span={6}>
                      <NumberInput
                        label="MA Period"
                        value={config.parameters.ma_period || 20}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, ma_period: Number(value) }
                        })}
                        disabled={isRunning}
                        min={5}
                        max={100}
                      />
                    </Grid.Col>
                    <Grid.Col span={6}>
                      <NumberInput
                        label="Threshold (%)"
                        value={config.parameters.threshold || 0.5}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, threshold: Number(value) }
                        })}
                        disabled={isRunning}
                        min={0.1}
                        max={5}
                        step={0.1}
                      />
                    </Grid.Col>
                  </>
                )}

                {config.type === 'rsi' && (
                  <>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="RSI Period"
                        value={config.parameters.rsi_period || 14}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, rsi_period: Number(value) }
                        })}
                        disabled={isRunning}
                        min={5}
                        max={50}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="RSI Oversold"
                        value={config.parameters.rsi_oversold || 30}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, rsi_oversold: Number(value) }
                        })}
                        disabled={isRunning}
                        min={10}
                        max={40}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="RSI Overbought"
                        value={config.parameters.rsi_overbought || 70}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, rsi_overbought: Number(value) }
                        })}
                        disabled={isRunning}
                        min={60}
                        max={90}
                      />
                    </Grid.Col>
                  </>
                )}

                {config.type === 'grid' && (
                  <>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="Grid Levels"
                        value={config.parameters.grid_levels || 10}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, grid_levels: Number(value) }
                        })}
                        disabled={isRunning}
                        min={3}
                        max={50}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="Grid Spacing (%)"
                        value={config.parameters.grid_spacing || 1}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, grid_spacing: Number(value) }
                        })}
                        disabled={isRunning}
                        min={0.1}
                        max={10}
                        step={0.1}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="Grid Amount ($)"
                        value={config.parameters.grid_amount || 100}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, grid_amount: Number(value) }
                        })}
                        disabled={isRunning}
                        min={10}
                        max={1000}
                      />
                    </Grid.Col>
                  </>
                )}

                {config.type === 'dca' && (
                  <>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="DCA Interval (seconds)"
                        value={config.parameters.dca_interval || 3600}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, dca_interval: Number(value) }
                        })}
                        disabled={isRunning}
                        min={60}
                        max={86400}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="DCA Amount ($)"
                        value={config.parameters.dca_amount || 50}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, dca_amount: Number(value) }
                        })}
                        disabled={isRunning}
                        min={10}
                        max={1000}
                      />
                    </Grid.Col>
                    <Grid.Col span={4}>
                      <NumberInput
                        label="Price Drop Trigger (%)"
                        value={config.parameters.dca_price_drop || 2}
                        onChange={(value) => setConfig({
                          ...config,
                          parameters: { ...config.parameters, dca_price_drop: Number(value) }
                        })}
                        disabled={isRunning}
                        min={0.5}
                        max={20}
                        step={0.1}
                      />
                    </Grid.Col>
                  </>
                )}

                {!config.type && (
                  <Grid.Col span={12}>
                    <Text c="dimmed" fs="italic" ta="center" py="xl">
                      Select a strategy type to configure parameters
                    </Text>
                  </Grid.Col>
                )}

                {config.type && !['simple_ma', 'rsi', 'grid', 'dca'].includes(config.type) && (
                  <Grid.Col span={12}>
                    <Text c="dimmed" fs="italic" ta="center" py="xl">
                      Parameters for "{config.type}" strategy are not yet implemented
                    </Text>
                  </Grid.Col>
                )}
              </Grid>
            </Accordion.Panel>
          </Accordion.Item>

          <Accordion.Item value="risk">
            <Accordion.Control>Risk Management</Accordion.Control>
            <Accordion.Panel>
              <Grid>
                <Grid.Col span={4}>
                  <NumberInput
                    label="Max Position Size ($)"
                    value={config.risk_management.max_position_size}
                    onChange={(value) => setConfig({
                      ...config,
                      risk_management: { 
                        ...config.risk_management, 
                        max_position_size: Number(value) 
                      }
                    })}
                    disabled={isRunning}
                    min={1}
                    max={10000}
                  />
                </Grid.Col>
                <Grid.Col span={4}>
                  <NumberInput
                    label="Stop Loss (%)"
                    value={config.risk_management.stop_loss_percentage}
                    onChange={(value) => setConfig({
                      ...config,
                      risk_management: { 
                        ...config.risk_management, 
                        stop_loss_percentage: Number(value) 
                      }
                    })}
                    disabled={isRunning}
                    min={0.1}
                    max={10}
                    step={0.1}
                  />
                </Grid.Col>
                <Grid.Col span={4}>
                  <NumberInput
                    label="Take Profit (%)"
                    value={config.risk_management.take_profit_percentage}
                    onChange={(value) => setConfig({
                      ...config,
                      risk_management: { 
                        ...config.risk_management, 
                        take_profit_percentage: Number(value) 
                      }
                    })}
                    disabled={isRunning}
                    min={0.1}
                    max={20}
                    step={0.1}
                  />
                </Grid.Col>
              </Grid>
            </Accordion.Panel>
          </Accordion.Item>
        </Accordion>

        <Card withBorder>
          <Stack gap="xs">
            <Text size="sm" fw={500}>Current Configuration Summary</Text>
            <Group>
              <Badge color="blue">{config.type}</Badge>
              <Badge color="green">{config.symbol}</Badge>
              <Badge color="orange">{config.timeframe}</Badge>
            </Group>
            <Text size="xs" c="dimmed">
              Risk: ${config.risk_management.max_position_size} max, 
              {config.risk_management.stop_loss_percentage}% SL, 
              {config.risk_management.take_profit_percentage}% TP
            </Text>
          </Stack>
        </Card>
      </Stack>
    </Paper>
  );
};

