import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Tabs, Paper, Group, Select, Title, Loader, Table, ScrollArea, Alert, Badge, Stack, Text, ActionIcon, Tooltip } from '@mantine/core';
import { getOrdersHistory, type OrderResponse, cancelOrder } from '../services/restClient';
import { useUserStream } from '../store/userStream';
import { FreshnessBadge } from './FreshnessBadge';
import { useAssets } from '../hooks/useAssets';
import { IconReload, IconX } from '@tabler/icons-react';

// No props needed - component now uses only WebSocket for real-time updates
interface OrdersPanelProps {}

// Status -> kolor mantine
const statusColorMap: Record<string, string> = {
  NEW: 'blue',
  FILLED: 'teal',
  PARTIALLY_FILLED: 'orange',
  CANCELED: 'red',
  EXPIRED: 'gray',
};

const numberFmt = new Intl.NumberFormat('pl-PL', { minimumFractionDigits: 2, maximumFractionDigits: 8 });
const timeFmt = (ts: number) => new Date(ts).toLocaleString('pl-PL');

const OrdersPanel: React.FC<OrdersPanelProps> = () => {
  const [tab, setTab] = useState<string>('open');
  const [filterSymbol, setFilterSymbol] = useState<string | null>(null); // null = ALL (lazy)
  const { state: userState, sendResnapshot, addOptimisticCancel } = useUserStream();
  const [historyItems, setHistoryItems] = useState<OrderResponse[]>([]);
  const [nextCursor, setNextCursor] = useState<number | null>(null);
  const [hasMore, setHasMore] = useState(true);
  const [loadingOpen, setLoadingOpen] = useState(false);
  const [loadingHistory, setLoadingHistory] = useState(false);
  const [loadingMore, setLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [errorMore, setErrorMore] = useState<string | null>(null);
  const [cancellingId, setCancellingId] = useState<number | null>(null);
  const [lastRefresh, setLastRefresh] = useState<Date | null>(null);
  // Finalne statusy do deduplikacji
  const FINAL_STATUSES = ['FILLED','CANCELED','REJECTED','EXPIRED'];

  const { assets } = useAssets();
  // Stabilizuj listƒô symboli ‚Äì zmieniaj tylko gdy faktycznie lista (a nie ceny) siƒô zmieni
  const symbols = useMemo(() => {
    const set = Array.from(new Set(assets.map(a => a.symbol)));
    set.sort();
    return set;
  }, [assets.map(a => a.symbol).join(',')]);
  const filterOptions = useMemo(() => (
    [{ value: 'ALL', label: 'Wszystkie' }, ...symbols.map(s => ({ value: s, label: s }))]
  ), [symbols]);

  // Pobierz otwarte zlecenia dla wszystkich (API bez parametru zwraca wszystkie dla konta)
  // open orders pobieramy z user stream state
  const openOrders: OrderResponse[] = useMemo(() => {
    const arr = Object.values(userState.openOrders || {});
      return arr.map(o => ({
        orderId: o.orderId,
        orderListId: (o as any).orderListId,
        clientOrderId: o.clientOrderId || '',
        symbol: o.symbol,
        side: o.side,
        type: o.type || '',
        price: o.price || '0',
        origQty: o.origQty || '0',
        executedQty: o.executedQty || '0',
        cummulativeQuoteQty: o.cummulativeQuoteQty || '0',
        status: o.status,
        timeInForce: o.timeInForce || '',
        time: o.updateTime || Date.now(),
        updateTime: o.updateTime || Date.now(),
        origQuoteOrderQty: (o as any).origQuoteOrderQty,
        stopPrice: (o as any).stopPrice,
        icebergQty: (o as any).icebergQty,
        isWorking: (o as any).isWorking,
      }));
  }, [userState.openOrders]);

  // Pobierz historiƒô dla wszystkich symboli sekwencyjnie (lub r√≥wnolegle Promise.all)
  // Pobierz pierwszƒÖ stronƒô historii
  const loadHistoryForSymbol = useCallback(async (symbol: string) => {
    setLoadingHistory(true); setError(null);
    try {
      const res = await getOrdersHistory(symbol, 50); // pierwsza strona
      const list = res?.orders || [];
      // Dedup: orderId + finalStatus
      const dedupMap = new Map<string, OrderResponse>();
      list.forEach(o => {
        const key = `${o.orderId}:${FINAL_STATUSES.includes(o.status) ? o.status : ''}`;
        if (!dedupMap.has(key)) dedupMap.set(key, o);
      });
      const deduped = Array.from(dedupMap.values());
      deduped.sort((a, b) => b.time - a.time);
      setHistoryItems(deduped);
      setNextCursor(deduped.length > 0 ? deduped[deduped.length-1].orderId : null);
      setHasMore(list.length === 50);
      setLastRefresh(new Date());
    } catch (e) {
      setError('B≈ÇƒÖd podczas pobierania historii zlece≈Ñ');
    } finally { setLoadingHistory(false); }
  }, []);

  // Pobierz kolejnƒÖ porcjƒô historii (paginacja)
  const fetchMoreHistory = useCallback(async () => {
    if (loadingMore || !hasMore || !filterSymbol) return;
    setLoadingMore(true); setErrorMore(null);
    try {
      const res = await getOrdersHistory(filterSymbol, 50, nextCursor || undefined);
      const list = res?.orders || [];
      // Dedup: orderId + finalStatus
      const dedupMap = new Map<string, OrderResponse>();
      [...historyItems, ...list].forEach(o => {
        const key = `${o.orderId}:${FINAL_STATUSES.includes(o.status) ? o.status : ''}`;
        if (!dedupMap.has(key)) dedupMap.set(key, o);
      });
      const deduped = Array.from(dedupMap.values());
      deduped.sort((a, b) => b.time - a.time);
      setHistoryItems(deduped);
      setNextCursor(list.length > 0 ? list[list.length-1].orderId : nextCursor);
      setHasMore(list.length === 50);
    } catch (e) {
      setErrorMore('B≈ÇƒÖd podczas ≈Çadowania starszych zlece≈Ñ');
    } finally { setLoadingMore(false); }
  }, [loadingMore, hasMore, filterSymbol, nextCursor, historyItems]);

  // Oddzielne efekty aby zmiana symboli (przez tickery) nie wywo≈Çywa≈Ça ponownie loadOpen
  useEffect(() => {
    if (tab === 'open') {
      setLoadingOpen(false);
      setLastRefresh(new Date());
    }
  }, [tab, userState.openOrders]);

  useEffect(() => {
    if (tab === 'history') {
      if (filterSymbol && filterSymbol !== 'ALL') {
        loadHistoryForSymbol(filterSymbol);
      } else {
        setHistoryItems([]);
        setNextCursor(null);
        setHasMore(false);
      }
    }
  }, [tab, filterSymbol, loadHistoryForSymbol]);

  // Disable legacy polling (interval removed)

  const handleManualRefresh = (() => {
    let last = 0;
    return () => {
      const now = Date.now();
      if (now - last < 5000) return; // throttle resnapshot to 5s (Phase 7 requirement)
      last = now;
      sendResnapshot();
    };
  })();

  const handleCancel = async (order: OrderResponse) => {
    setCancellingId(order.orderId);
    setError(null);

    try {
      // Optymistyczne oznaczenie jako CANCELED
      addOptimisticCancel(order.orderId, 10000); // 10s timeout dla rollback

      const resp = await cancelOrder(order.orderId, order.symbol);
      if (resp?.error) {
        setError(resp.error);
        // Uwaga: Rollback zostanie wykonany automatycznie po timeout je≈õli nie przyjdzie delta
      }
    } catch (e) {
      setError('B≈ÇƒÖd anulowania zlecenia');
      // Uwaga: Rollback zostanie wykonany automatycznie po timeout je≈õli nie przyjdzie delta
    } finally { 
      setCancellingId(null); 
    }
  };

  const applyFilter = (orders: OrderResponse[]) => {
    if (!filterSymbol || filterSymbol === 'ALL') return orders; // open orders mo≈ºe byƒá filtrowane globalnie
    return orders.filter(o => o.symbol === filterSymbol);
  };

  const renderRows = (orders: OrderResponse[], showStatus: boolean, allowCancel: boolean) => {
    const filtered = applyFilter(orders);
    if (!filtered.length) {
      return (
        <Table.Tr>
          <Table.Td colSpan={allowCancel ? 9 : showStatus ? 8 : 7}>
            <Text ta="center" c="dimmed">Brak zlece≈Ñ</Text>
          </Table.Td>
        </Table.Tr>
      );
    }
    return filtered.map(o => (
      <Table.Tr key={`${o.symbol}-${o.orderId}-${FINAL_STATUSES.includes(o.status) ? o.status : ''}`}>
        <Table.Td>{o.symbol}</Table.Td>
        <Table.Td>{o.type}</Table.Td>
        <Table.Td c={o.side === 'BUY' ? 'teal' : 'red'} fw={500}>{o.side}</Table.Td>
        <Table.Td ta="right" ff="monospace">{numberFmt.format(parseFloat(o.price || '0'))}</Table.Td>
        <Table.Td ta="right" ff="monospace">{numberFmt.format(parseFloat(o.origQty || '0'))}</Table.Td>
        <Table.Td ta="right" ff="monospace">{numberFmt.format(parseFloat(o.executedQty || '0'))}</Table.Td>
        {showStatus && (
          <Table.Td>
            <Badge color={statusColorMap[o.status] || 'gray'} variant="light" size="sm">{o.status}</Badge>
          </Table.Td>
        )}
        <Table.Td>{timeFmt(o.time)}</Table.Td>
        {allowCancel && (
          <Table.Td>
            <Tooltip label="Anuluj zlecenie">
              <ActionIcon variant="subtle" color="red" size="sm" onClick={() => handleCancel(o)} loading={cancellingId === o.orderId}>
                <IconX size={14} />
              </ActionIcon>
            </Tooltip>
          </Table.Td>
        )}
      </Table.Tr>
    ));
  };

  const filterLabel = filterSymbol && filterSymbol !== 'ALL' ? ` (filtr: ${filterSymbol})` : '';

  return (
    <Stack p="md" gap="md">
      <Group justify="space-between" align="center">
        <Title order={2}>ZarzƒÖdzanie Zleceniami</Title>
        <Group gap="xs">
          <Select
            data={filterOptions}
            value={filterSymbol || 'ALL'}
            onChange={v => setFilterSymbol(v === 'ALL' ? null : v)}
            placeholder="Filtr Symbolu"
            searchable
            w={200}
            label="Filtr Symbolu"
            data-testid="filter-select"
          />
          <Tooltip label="Od≈õwie≈º">
            <ActionIcon variant="light" onClick={handleManualRefresh}>
              <IconReload size={16} />
            </ActionIcon>
          </Tooltip>
        </Group>
      </Group>

      {error && (
        <Alert color="red" variant="light" withCloseButton onClose={() => setError(null)} title="B≈ÇƒÖd">
          {error}
        </Alert>
      )}

      <Tabs value={tab} onChange={(v) => setTab(v || 'open')} keepMounted={false}>
        <Tabs.List>
          <Tabs.Tab value="open">Otwarte Zlecenia</Tabs.Tab>
          <Tabs.Tab value="history">Historia Zlece≈Ñ</Tabs.Tab>
        </Tabs.List>

        <Tabs.Panel value="open" pt="md">
          <Paper withBorder p="sm">
            <Group justify="space-between" mb="sm">
              <Group gap={6}>
                <Title order={4}>Otwarte Zlecenia{filterLabel}</Title>
                {loadingOpen && <Loader size="sm" />}
              </Group>
               {lastRefresh && (
                 <FreshnessBadge 
                   freshnessMs={userState.freshnessMs ?? 0}
                   fallback={userState.fallback === true}
                 />
               )}
            </Group>
            <ScrollArea h={360} type="auto">
              <Table striped highlightOnHover withTableBorder withColumnBorders>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Symbol</Table.Th>
                    <Table.Th>Typ</Table.Th>
                    <Table.Th>Strona</Table.Th>
                    <Table.Th ta="right">Cena</Table.Th>
                    <Table.Th ta="right">Ilo≈õƒá</Table.Th>
                    <Table.Th ta="right">Wykonano</Table.Th>
                    <Table.Th>Czas</Table.Th>
                    <Table.Th>Akcje</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {loadingOpen ? (
                    <Table.Tr><Table.Td colSpan={8}><Group justify="center"><Loader /></Group></Table.Td></Table.Tr>
                  ) : (
                    renderRows(openOrders, false, true)
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
          </Paper>
        </Tabs.Panel>

        <Tabs.Panel value="history" pt="md">
          <Paper withBorder p="sm">
            <Group justify="space-between" mb="sm">
              <Group gap={6}>
                <Title order={4}>Historia Zlece≈Ñ{filterLabel}</Title>
                {loadingHistory && <Loader size="sm" />}
              </Group>
              {lastRefresh && historyItems.length > 0 && <Text size="xs" c="dimmed">Od≈õwie≈ºono: {lastRefresh.toLocaleTimeString()}</Text>}
            </Group>
            <ScrollArea h={360} type="auto">
              <Table striped highlightOnHover withTableBorder withColumnBorders>
                <Table.Thead>
                  <Table.Tr>
                    <Table.Th>Symbol</Table.Th>
                    <Table.Th>Typ</Table.Th>
                    <Table.Th>Strona</Table.Th>
                    <Table.Th ta="right">Cena</Table.Th>
                    <Table.Th ta="right">Ilo≈õƒá</Table.Th>
                    <Table.Th ta="right">Wykonano</Table.Th>
                    <Table.Th>Status</Table.Th>
                    <Table.Th>Czas</Table.Th>
                  </Table.Tr>
                </Table.Thead>
                <Table.Tbody>
                  {filterSymbol === null || filterSymbol === 'ALL' ? (
                    <Table.Tr><Table.Td colSpan={8}><Text ta="center" c="dimmed">Wybierz symbol aby za≈Çadowaƒá historiƒô (brak masowego pobierania wszystkich par)</Text></Table.Td></Table.Tr>
                  ) : loadingHistory ? (
                    <Table.Tr><Table.Td colSpan={8}><Group justify="center"><Loader /></Group></Table.Td></Table.Tr>
                  ) : (
                    renderRows(historyItems, true, false)
                  )}
                </Table.Tbody>
              </Table>
            </ScrollArea>
            {/* Przycisk "Za≈Çaduj wiƒôcej" */}
            {filterSymbol && filterSymbol !== 'ALL' && historyItems.length > 0 && hasMore && (
              <Group justify="center" mt="md">
                <button
                  disabled={loadingMore || !hasMore}
                  style={{ padding: '8px 24px', borderRadius: 6, background: '#eee', cursor: loadingMore || !hasMore ? 'not-allowed' : 'pointer', fontWeight: 500 }}
                  onClick={fetchMoreHistory}
                >
                  {loadingMore ? '≈Åadowanie...' : 'Za≈Çaduj wiƒôcej'}
                </button>
              </Group>
            )}
            {errorMore && (
              <Group justify="center" mt="xs"><Text c="red" size="sm">{errorMore}</Text></Group>
            )}
          </Paper>
        </Tabs.Panel>
      </Tabs>
    </Stack>
  );
};

export default OrdersPanel;

import React, { useState, useMemo, useEffect, memo } from 'react';
import {
  Text,
  Table,
  Paper,
  Group,
  Loader,
  Badge,
  ActionIcon,
  Box,
  Switch,
  Button,
  Stack,
  Progress,
  Select,
  TextInput,
  Tabs,
  Tooltip,
} from '@mantine/core';
import { 
  getCoreRowModel, 
  flexRender, 
  createColumnHelper, 
  useReactTable,
  getSortedRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  type SortingState,
  type ColumnFiltersState,
} from '@tanstack/react-table';
import { IconRefresh, IconSearch, IconSortAscending, IconSortDescending, IconEye, IconEyeOff } from '@tabler/icons-react';
import { PriceCell } from './shared';
import type { PortfolioBalance, PortfolioTableProps } from '../types/portfolio';
import { useDebounced } from '../hooks/useDebounced';
import { usePriceChangeAnimation } from '../hooks/usePriceChangeAnimation';
import { formatCurrency, formatCrypto } from '../types/portfolio';

const columnHelper = createColumnHelper<PortfolioBalance>();

const PortfolioTable: React.FC<PortfolioTableProps> = ({
  balances,
  loading = false,
  error = null,
  onRefresh,
  hideZeroBalances = true,
  onHideZeroBalancesChange,
}) => {
  // Stan dla sortowania, filtrowania i paginacji
  const [sorting, setSorting] = useState<SortingState>([
    { id: 'valueUSD', desc: true } // Sortuj po warto≈õci domy≈õlnie
  ]);
  const [columnFilters, setColumnFilters] = useState<ColumnFiltersState>([]);
  const [globalFilter, setGlobalFilter] = useState('');
  const [pagination, setPagination] = useState({
    pageIndex: 0,
    pageSize: 10,
  });

  // Debounced search dla lepszej wydajno≈õci
  const debouncedGlobalFilter = useDebounced(globalFilter, 300);

  // Zak≈Çadki kategorii: ALL | CRYPTO | STABLE | FIAT
  type View = 'ALL' | 'CRYPTO' | 'STABLE' | 'FIAT';
  const [view, setView] = useState<View>('ALL');

  // Zbiory klasyfikacji
  const STABLECOINS = useMemo(() => new Set([
    'USDT','USDC','BUSD','TUSD','USDP','DAI','FDUSD','EURT','PYUSD','USDD','GUSD'
  ]), []);
  // Rozszerzona lista fiat (kody ISO + spotykane na Binance w saldach)
  const FIAT_ASSETS = useMemo(() => new Set([
    'USD','EUR','GBP','PLN','JPY','CNY','TRY','BRL','ARS','MXN','ZAR','UAH','RON','KZT','NGN',
    'CZK','CHF','SEK','NOK','DKK','HUF','AUD','NZD','CAD','HKD','SGD','COP','CLP','PEN','PHP',
    'IDR','INR','THB','VND','ILS','AED','SAR','QAR','KRW','MYR'
  ]), []);

  // Klasyfikacja balans√≥w (dodajemy pole category)
  const classifiedBalances = useMemo(() => {
    if (!Array.isArray(balances)) return [] as (PortfolioBalance & { category: View })[];
    return balances.map(b => {
      const asset = (b.asset || '').toUpperCase();
      let category: View = 'CRYPTO';
      if (STABLECOINS.has(asset)) category = 'STABLE';
      else if (FIAT_ASSETS.has(asset)) category = 'FIAT';
      else {
        // Heurystyka: brak ceny + 3 litery + valueUSD 0 => prawdopodobnie fiat
        const noPrice = !b.currentPrice || b.currentPrice === 0;
        if (noPrice && asset.length === 3 && (b.valueUSD === 0 || b.valueUSD === undefined)) {
          category = 'FIAT';
        }
      }
      return { ...b, category };
    });
  }, [balances, STABLECOINS, FIAT_ASSETS]);

  // Hook do animacji zmian cen
  const priceChanges = usePriceChangeAnimation(
    Array.isArray(classifiedBalances) ? classifiedBalances.map(b => ({ symbol: b.asset, price: b.currentPrice || 0 })) : []
  );

  // Filter balances based on hideZeroBalances setting
  const filteredBalances = useMemo(() => {
    let list = classifiedBalances;
    if (hideZeroBalances) {
      list = list.filter(balance => balance.total > 0.00000001);
    }
    if (view !== 'ALL') {
      list = list.filter(b => b.category === view);
    }
    return list;
  }, [classifiedBalances, hideZeroBalances, view]);

  // Calculate total portfolio value for percentage calculations
  const totalPortfolioValue = useMemo(() => {
    return filteredBalances.reduce((sum, balance) => sum + (balance.valueUSD || 0), 0);
  }, [filteredBalances]);

  // Definicja kolumn dla tabeli
  const columns = useMemo(
    () => [
      columnHelper.accessor('asset', {
        header: 'Aktywo',
        cell: (info) => {
          const asset = info.getValue();
          const balance = info.row.original;
          const category = (balance as any).category as View;
          const isFiat = category === 'FIAT';
          const isStable = category === 'STABLE';
          const micaCompliance = balance.micaCompliance;

          return (
            <Group gap="xs">
              <Text data-testid={`asset-${asset}`} fw={600} c={isFiat ? 'orange' : isStable ? 'teal' : 'blue'} size="sm">
                {asset}
              </Text>
              {isFiat && (
                <Tooltip label="Fiat ‚Äì brak live wyceny" withArrow>
                  <Badge size="xs" color="gray" variant="light">FIAT</Badge>
                </Tooltip>
              )}
              {isStable && (
                <Tooltip label="Stablecoin" withArrow>
                  <Badge size="xs" color="teal" variant="light">STB</Badge>
                </Tooltip>
              )}
              {micaCompliance && micaCompliance.status !== 'UNKNOWN' && (
                <Tooltip 
                  label={`${micaCompliance.recommendation}${micaCompliance.delistingDate ? ` (${micaCompliance.delistingDate})` : ''}`} 
                  withArrow
                  multiline
                  w={300}
                >
                  <Badge 
                    size="xs" 
                    color={micaCompliance.status === 'COMPLIANT' ? 'green' : 'red'} 
                    variant="light"
                  >
                    {micaCompliance.status === 'COMPLIANT' ? 'MiCA ‚úì' : 'EU ‚ö†'}
                  </Badge>
                </Tooltip>
              )}
            </Group>
          );
        },
      }),
      columnHelper.accessor('free', {
        header: () => (
          <Tooltip label="Dostƒôpne ≈õrodki do handlu (nie zawiera zablokowanych w zleceniach)" withArrow>
            <Text component="span" style={{ textDecoration: 'underline dotted' }}>
              Dostƒôpne
            </Text>
          </Tooltip>
        ),
        cell: (info) => (
          <Text ta="right" ff="monospace" size="sm" c="teal">
            {formatCrypto(info.getValue(), 8)}
          </Text>
        ),
      }),
      columnHelper.accessor('locked', {
        header: () => (
          <Tooltip label="≈örodki zablokowane w aktywnych zleceniach" withArrow>
            <Text component="span" style={{ textDecoration: 'underline dotted' }}>
              Zablokowane
            </Text>
          </Tooltip>
        ),
        cell: (info) => {
          const value = info.getValue();
          if (value === 0) {
            return <Text ta="right" ff="monospace" size="sm" c="dimmed">-</Text>;
          }
          return (
            <Text ta="right" ff="monospace" size="sm" c="orange">
              {formatCrypto(value, 8)}
            </Text>
          );
        },
      }),
      columnHelper.accessor('total', {
        header: () => (
          <Tooltip label="≈ÅƒÖczne saldo (dostƒôpne + zablokowane)" withArrow>
            <Text component="span" style={{ textDecoration: 'underline dotted' }}>
              ≈ÅƒÖcznie
            </Text>
          </Tooltip>
        ),
        cell: (info) => (
          <Text ta="right" ff="monospace" size="sm" fw={600}>
            {formatCrypto(info.getValue(), 8)}
          </Text>
        ),
      }),
      columnHelper.accessor('currentPrice', {
        header: 'Cena',
        cell: (info) => {
          const asset = info.row.original.asset;
          const change = priceChanges.get(asset);
          const price = info.getValue();

          if (!price || price === 0) {
            const cat = (info.row.original as any).category as View;
            return (
              <Tooltip label={cat === 'FIAT' ? 'Fiat ‚Äì wycena niedostƒôpna' : 'Brak danych ceny'} withArrow>
                <Text c="dimmed" size="sm">-</Text>
              </Tooltip>
            );
          }

          return (
            <PriceCell 
              price={price}
              change={change || undefined}
              isUSDT={asset === 'USDT'}
            />
          );
        },
  sortingFn: 'basic',
      }),
      columnHelper.accessor('valueUSD', {
        header: 'Warto≈õƒá USD',
        cell: (info) => {
          const value = info.getValue();
          if (!value || value === 0) {
            return <Text c="dimmed" size="sm">$0.00</Text>;
          }
          return (
            <Text ta="right" ff="monospace" fw={500} size="sm">
              {formatCurrency(value, 'USD')}
            </Text>
          );
        },
  sortingFn: 'basic',
      }),
      // Kolumna udzia≈Çu procentowego w portfelu ‚Äì teraz sortowalna
      columnHelper.accessor(
        row => {
          const value = row.valueUSD || 0;
          if (totalPortfolioValue === 0) return 0;
            return (value / totalPortfolioValue) * 100;
        },
        {
          id: 'allocation',
          header: '%',
          enableSorting: true,
          // Funkcja sortujƒÖca ‚Äì proste por√≥wnanie numeryczne
          sortingFn: 'basic',
          cell: (info) => {
            const percentage = info.getValue<number>() || 0;
            return (
              <Box style={{ minWidth: 60 }}>
                <Text size="xs" c="dimmed" mb={2}>
                  {percentage.toFixed(1)}%
                </Text>
                <Progress
                  value={percentage}
                  size="xs"
                  color={percentage > 10 ? 'blue' : percentage > 5 ? 'teal' : 'gray'}
                />
              </Box>
            );
          },
        }
      ),
    ],
    [priceChanges, totalPortfolioValue]
  );

  // Konfiguracja tabeli TanStack
  const baseGetSortedRowModel = getSortedRowModel();
  const table = useReactTable({
    data: filteredBalances,
    columns,
    state: {
      sorting,
      columnFilters,
      globalFilter: debouncedGlobalFilter,
      pagination,
    },
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    onGlobalFilterChange: (value) => {
      setGlobalFilter(value as string);
      // Po zmianie wyszukiwarki przejd≈∫ na pierwszƒÖ stronƒô, ≈ºeby uniknƒÖƒá pustego widoku
      setPagination(prev => ({ ...prev, pageIndex: 0 }));
    },
    onPaginationChange: setPagination,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: ((table: any) => {
      const sortedModel = baseGetSortedRowModel(table) as any;
      const sortingState = table.getState().sorting as any[];
      if (!sortingState.length) return sortedModel;
      const colId = sortingState[sortingState.length - 1].id;
  if (!['currentPrice','valueUSD','allocation','priceChange24h','valueChange24h'].includes(colId)) return sortedModel;
      const rows: any[] = sortedModel.rows || [];
      if (!rows.length) return sortedModel;
      const nonZero: any[] = [];
      const zeros: any[] = [];
      for (const r of rows) {
        const raw = r.getValue(colId);
        const v = typeof raw === 'number' ? raw : parseFloat(raw);
        if (!v) zeros.push(r); else nonZero.push(r);
      }
      sortedModel.rows = [...nonZero, ...zeros];
      return sortedModel;
    }) as any,
    getFilteredRowModel: getFilteredRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    // Zapobiegnij skakaniu na pierwszƒÖ stronƒô przy ka≈ºdej aktualizacji cen
    autoResetPageIndex: false,
    // Stabilne ID wiersza ‚Äì aktywo jest unikalne
    getRowId: (row) => row.asset,
  debugTable: (typeof process !== 'undefined' && (process as any).env && (process as any).env.NODE_ENV === 'development') || false,
  });

  // Post-processing: przenie≈õ wiersze z zerowƒÖ warto≈õciƒÖ/cenƒÖ/udzia≈Çem na d√≥≈Ç niezale≈ºnie od kierunku
  const displayRows = useMemo(() => {
    const sortingState = table.getState().sorting;
    if (!sortingState.length) return table.getRowModel().rows;
    const colId = sortingState[sortingState.length - 1].id;
    if (!['currentPrice','valueUSD','allocation','priceChange24h','valueChange24h'].includes(colId)) {
      return table.getRowModel().rows;
    }
    const rows = table.getRowModel().rows;
    const nonZero: typeof rows = [];
    const zeros: typeof rows = [];
    for (const r of rows) {
      const raw = r.getValue(colId) as any;
      const v = typeof raw === 'number' ? raw : parseFloat(raw);
      if (!v) zeros.push(r); else nonZero.push(r);
    }
    return [...nonZero, ...zeros];
  }, [table, table.getState().sorting, table.getRowModel()]);

  // Korekta pageIndex je≈õli po zmianie liczby element√≥w wskazuje poza zakresem
  useEffect(() => {
    const pageCount = table.getPageCount();
    setPagination(prev => {
      if (prev.pageIndex > 0 && prev.pageIndex >= pageCount) {
        return { ...prev, pageIndex: Math.max(0, pageCount - 1) };
      }
      return prev;
    });
  }, [filteredBalances.length, pagination.pageSize, table]);

  if (loading) {
    return (
      <Paper p="md" withBorder>
        <Group justify="center">
          <Loader size="md" />
          <Text>≈Åadowanie portfolio...</Text>
        </Group>
      </Paper>
    );
  }

  if (error) {
    return (
      <Paper p="md" withBorder>
        <Group justify="center" c="red">
          <Text>B≈ÇƒÖd: {error}</Text>
          {onRefresh && (
            <ActionIcon variant="outline" color="red" onClick={onRefresh}>
              <IconRefresh size={16} />
            </ActionIcon>
          )}
        </Group>
      </Paper>
    );
  }

  const filteredCount = table.getFilteredRowModel().rows.length;
  const totalCount = classifiedBalances.length;

  return (
    <Stack gap="md">
      {/* Kontrolki i statystyki */}
      <Paper p="md" withBorder>
        <Stack gap="sm">
          <Group justify="space-between">
            <Text size="lg" fw={600}>
              Portfolio Holdings
            </Text>
            <Group gap="xs">
              <Text size="sm" c="dimmed">
                {filteredCount} aktyw√≥w{(view !== 'ALL' || globalFilter) && ` (z ${totalCount})`}
              </Text>
              <Text size="sm" fw={500}>
                {formatCurrency(totalPortfolioValue)}
              </Text>
            </Group>
          </Group>

          <Tabs value={view} onChange={(v) => setView((v as View) || 'ALL')} keepMounted={false} variant="outline" radius="sm">
            <Tabs.List>
              <Tabs.Tab value="ALL">Wszystko</Tabs.Tab>
              <Tabs.Tab value="CRYPTO">Krypto</Tabs.Tab>
