export default memo(PortfolioTable);

import React, { useState, useEffect, memo } from 'react';
import {
  Paper,
  Stack,
  Group,
  Text,
  Title,
  Button,
  NumberInput,
  Select,
  Alert,
  Badge,
  Divider,
  Grid,
  Tooltip,
} from '@mantine/core';
import { 
  IconTrendingUp, 
  IconTrendingDown, 
  IconFlask,
  IconAlertCircle,
  IconCheck,
  IconWallet,
} from '@tabler/icons-react';
import { placeOrder, testOrder, type PlaceOrderRequest } from '../services/restClient';
import { getCurrentTicker } from '../services/restClient';
import { useAssets } from '../hooks/useAssets';
import { useUserStream } from '../store/userStream';
import { usePortfolio } from '../hooks/usePortfolio';
import { formatCrypto } from '../types/portfolio';

interface TickerData {
  symbol: string;
  price: string;
  change: string;
  changePercent: string;
}

const TradingPanel: React.FC = () => {
  // Form state
  const [symbol, setSymbol] = useState('BTCUSDT');
  const [side, setSide] = useState<'BUY' | 'SELL'>('BUY');
  const [orderType, setOrderType] = useState<'MARKET' | 'LIMIT'>('MARKET');
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [timeInForce, setTimeInForce] = useState<'GTC' | 'IOC' | 'FOK'>('GTC');

  // UI state
  const [loading, setLoading] = useState(false);
  const [testLoading, setTestLoading] = useState(false);
  const [result, setResult] = useState<any>(null);
  const [error, setError] = useState<string | null>(null);
  const [ticker, setTicker] = useState<TickerData | null>(null);

  // Assets hook for symbol selection and real-time ticker data
  const { assets } = useAssets();

  // Portfolio hook for balance information
  const { balances } = usePortfolio();

  // User stream for optimistic updates
  const { addPendingOrder } = useUserStream();

  // Get ticker data from assets instead of polling
  const currentAsset = assets.find(asset => asset.symbol === symbol);

  useEffect(() => {
    if (currentAsset) {
      setTicker({
        symbol: currentAsset.symbol,
        price: currentAsset.price.toString(),
        change: currentAsset.priceChange?.toString() || '0',
        changePercent: `${currentAsset.priceChangePercent?.toFixed(2) || '0'}%`
      });
    }
  }, [currentAsset]);

  // Fallback: Load ticker data only once on mount if no WebSocket data
  useEffect(() => {
    if (!currentAsset && symbol) {
      const loadTicker = async () => {
        try {
          const tickerData = await getCurrentTicker(symbol);
          if (tickerData) {
            setTicker({
              symbol: tickerData.symbol,
              price: tickerData.price,
              change: '0',
              changePercent: '0%'
            });
          }
        } catch (err) {
          console.error('Failed to load ticker:', err);
        }
      };
      loadTicker();
    }
  }, [symbol, currentAsset]);

  const handleTestOrder = async () => {
    setTestLoading(true);
    setError(null);
    setResult(null);

    try {
      const orderData: PlaceOrderRequest = {
        symbol,
        side,
        type: orderType,
        quantity: quantity.toString(),
        ...(orderType === 'LIMIT' && { price: price.toString() }),
        timeInForce
      };

      const response = await testOrder(orderData);

      if (response.success) {
        setResult({
          type: 'test',
          message: response.message,
          data: response.test_result
        });
      } else {
        setError(response.error || 'Test order failed');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Test order failed');
    } finally {
      setTestLoading(false);
    }
  };

  const handlePlaceOrder = async () => {
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      const orderData: PlaceOrderRequest = {
        symbol,
        side,
        type: orderType,
        quantity: quantity.toString(),
        ...(orderType === 'LIMIT' && { price: price.toString() }),
        timeInForce
      };

      // Optymistyczne dodanie zlecenia ze statusem PENDING
      const optimisticOrder = {
        orderId: Math.floor(Math.random() * 1000000), // Tymczasowe ID
        clientOrderId: `optimistic_${Date.now()}`,
        symbol,
        side,
        type: orderType,
        timeInForce,
        price: orderType === 'LIMIT' ? price.toString() : ticker?.price || '0',
        origQty: quantity.toString(),
        executedQty: '0',
        cummulativeQuoteQty: '0',
        avgPrice: '0',
        status: 'PENDING',
        updateTime: Date.now(),
        fills: []
      };

      addPendingOrder(optimisticOrder, 5000); // 5s timeout

      const response = await placeOrder(orderData);

      if (response.success) {
        setResult({
          type: 'live',
          message: 'Order placed successfully!',
          data: response.order
        });
        // Reset form after successful order
        setQuantity('');
        setPrice('');
      } else {
        setError(response.error || 'Failed to place order');
        // Note: Optymistyczne zlecenie zostanie automatycznie usuniƒôte po timeout
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to place order');
      // Note: Optymistyczne zlecenie zostanie automatycznie usuniƒôte po timeout
    } finally {
      setLoading(false);
    }
  };

  const isFormValid = () => {
    if (!quantity || parseFloat(quantity.toString()) <= 0) return false;
    if (orderType === 'LIMIT' && (!price || parseFloat(price.toString()) <= 0)) return false;
    return true;
  };

  const getEstimatedTotal = () => {
    if (!quantity) return '0.00';

    let estimatedPrice = 0;
    if (orderType === 'LIMIT' && price) {
      estimatedPrice = parseFloat(price.toString());
    } else if (ticker?.price) {
      estimatedPrice = parseFloat(ticker.price);
    }

    const qty = parseFloat(quantity.toString());
    return (qty * estimatedPrice).toFixed(2);
  };

  const symbolOptions = assets.map(asset => ({
    value: asset.symbol,
    label: `${asset.symbol} - ${asset.baseAsset}/${asset.quoteAsset}`
  }));

  return (
    <Stack gap="md" p="md">
      <Title order={2}>Panel Tradingowy</Title>

      {/* Current Price Display */}
      {ticker && (
        <Paper p="md" withBorder>
          <Group justify="space-between" align="center">
            <Group gap="md">
              <Text size="xl" fw={700}>
                {ticker.symbol}
              </Text>
              <Text size="lg" ff="monospace" fw={600}>
                ${parseFloat(ticker.price).toFixed(2)}
              </Text>
            </Group>
            <Badge color="blue" variant="light">
              TESTNET
            </Badge>
          </Group>
        </Paper>
      )}

      {/* Available Balance Display */}
      <Paper p="md" withBorder>
        <Group justify="space-between" align="center">
          <Group gap="md">
            <IconWallet size={20} />
            <Text size="md" fw={600}>
              Dostƒôpne saldo
            </Text>
          </Group>
          <Group gap="lg">
            {/* Show USDT balance for all pairs */}
            <Group gap="xs">
              <Text size="sm" c="dimmed">USDT:</Text>
              <Tooltip label="Dostƒôpne ≈õrodki do handlu (nie zawiera zablokowanych w zleceniach)" withArrow>
                <Text size="sm" ff="monospace" fw={600} c="teal" style={{ textDecoration: 'underline dotted' }}>
                  {(() => {
                    const usdtBalance = balances.find(b => b.asset === 'USDT');
                    return usdtBalance ? formatCrypto(usdtBalance.free, 2) : '0.00';
                  })()}
                </Text>
              </Tooltip>
            </Group>

            {/* Show base asset balance if not USDT */}
            {symbol && !symbol.endsWith('USDT') && (() => {
              const baseAsset = symbol.replace('USDT', '');
              const baseBalance = balances.find(b => b.asset === baseAsset);
              return baseBalance && baseBalance.free > 0 ? (
                <Group gap="xs">
                  <Text size="sm" c="dimmed">{baseAsset}:</Text>
                  <Tooltip label="Dostƒôpne ≈õrodki do sprzeda≈ºy" withArrow>
                    <Text size="sm" ff="monospace" fw={600} c="blue" style={{ textDecoration: 'underline dotted' }}>
                      {formatCrypto(baseBalance.free, 8)}
                    </Text>
                  </Tooltip>
                </Group>
              ) : null;
            })()}
          </Group>
        </Group>
      </Paper>

      <Grid>
        <Grid.Col span={8}>
          <Paper p="md" withBorder>
            <Stack gap="md">
              <Text size="lg" fw={600}>
                Nowe Zlecenie
              </Text>

              {/* Symbol Selection */}
              <Select
                label="Symbol"
                value={symbol}
                onChange={(value) => setSymbol(value || 'BTCUSDT')}
                data={symbolOptions}
                searchable
                maxDropdownHeight={200}
              />

              {/* Order Type and Side */}
              <Grid>
                <Grid.Col span={6}>
                  <Select
                    label="Typ zlecenia"
                    value={orderType}
                    onChange={(value) => setOrderType(value as 'MARKET' | 'LIMIT')}
                    data={[
                      { value: 'MARKET', label: 'Market (natychmiastowe)' },
                      { value: 'LIMIT', label: 'Limit (z cenƒÖ)' }
                    ]}
                  />
                </Grid.Col>
                <Grid.Col span={6}>
                  <Select
                    label="Strona"
                    value={side}
                    onChange={(value) => setSide(value as 'BUY' | 'SELL')}
                    data={[
                      { value: 'BUY', label: 'üü¢ Kupno (BUY)' },
                      { value: 'SELL', label: 'üî¥ Sprzeda≈º (SELL)' }
                    ]}
                  />
                </Grid.Col>
              </Grid>

              {/* Quantity and Price */}
              <Grid>
                <Grid.Col span={orderType === 'LIMIT' ? 6 : 12}>
                  <NumberInput
                    label={`Ilo≈õƒá (${symbol.replace('USDT', '')})`}
                    value={quantity}
                    onChange={setQuantity}
                    min={0}
                    decimalScale={8}
                    placeholder="0.001"
                    required
                  />
                </Grid.Col>
                {orderType === 'LIMIT' && (
                  <Grid.Col span={6}>
                    <NumberInput
                      label="Cena (USDT)"
                      value={price}
                      onChange={setPrice}
                      min={0}
                      decimalScale={2}
                      placeholder={ticker?.price || '0.00'}
                      required
                    />
                  </Grid.Col>
                )}
              </Grid>

              {/* Time in Force (only for LIMIT orders) */}
              {orderType === 'LIMIT' && (
                <Select
                  label="Time in Force"
                  value={timeInForce}
                  onChange={(value) => setTimeInForce(value as 'GTC' | 'IOC' | 'FOK')}
                  data={[
                    { value: 'GTC', label: 'GTC - Good Till Canceled' },
                    { value: 'IOC', label: 'IOC - Immediate or Cancel' },
                    { value: 'FOK', label: 'FOK - Fill or Kill' }
                  ]}
                />
              )}

              {/* Estimated Total */}
              <Paper p="sm" bg="gray.0" withBorder>
                <Group justify="space-between">
                  <Text size="sm" c="dimmed">
                    Szacowana warto≈õƒá:
                  </Text>
                  <Text size="sm" fw={600} ff="monospace">
                    ${getEstimatedTotal()} USDT
                  </Text>
                </Group>
              </Paper>

              <Divider />

              {/* Action Buttons */}
              <Group gap="md">
                <Button
                  leftSection={<IconFlask size={16} />}
                  variant="outline"
                  onClick={handleTestOrder}
                  loading={testLoading}
                  disabled={!isFormValid() || loading}
                  flex={1}
                >
                  Test Order
                </Button>
                <Button
                  leftSection={side === 'BUY' ? <IconTrendingUp size={16} /> : <IconTrendingDown size={16} />}
                  color={side === 'BUY' ? 'teal' : 'red'}
                  onClick={handlePlaceOrder}
                  loading={loading}
                  disabled={!isFormValid() || testLoading}
                  flex={1}
                >
                  {side === 'BUY' ? 'Kupuj' : 'Sprzedaj'}
                </Button>
              </Group>
            </Stack>
          </Paper>
        </Grid.Col>

        <Grid.Col span={4}>
          <Stack gap="md">
            {/* Quick Actions */}
            <Paper p="md" withBorder>
              <Text size="md" fw={600} mb="md">
                Szybkie akcje
              </Text>
              <Stack gap="xs">
                <Button
                  size="sm"
                  variant="light"
                  onClick={() => {
                    setQuantity('0.001');
                    setOrderType('MARKET');
                    setSide('BUY');
                  }}
                >
                  Kup 0.001 BTC (Market)
                </Button>
                <Button
                  size="sm"
                  variant="light"
                  color="red"
                  onClick={() => {
                    setQuantity('0.001');
                    setOrderType('MARKET');
                    setSide('SELL');
                  }}
                >
                  Sprzedaj 0.001 BTC (Market)
                </Button>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => {
                    setQuantity('');
                    setPrice('');
                    setOrderType('MARKET');
                    setSide('BUY');
                    setError(null);
                    setResult(null);
                  }}
                >
                  Wyczy≈õƒá formularz
                </Button>
              </Stack>
            </Paper>

            {/* Info */}
            <Paper p="md" withBorder>
              <Text size="sm" c="dimmed">
                ‚ö†Ô∏è To jest ≈õrodowisko testowe Binance. Zlecenia sƒÖ wykonywane na testnet z wirtualnymi ≈õrodkami.
              </Text>
            </Paper>
          </Stack>
        </Grid.Col>
      </Grid>

      {/* Results Display */}
      {error && (
        <Alert
          icon={<IconAlertCircle size={16} />}
          title="B≈ÇƒÖd"
          color="red"
          onClose={() => setError(null)}
          withCloseButton
        >
          {error}
        </Alert>
      )}

      {result && (
        <Alert
          icon={<IconCheck size={16} />}
          title={result.type === 'test' ? 'Test Order - Sukces' : 'Zlecenie z≈Ço≈ºone'}
          color={result.type === 'test' ? 'blue' : 'teal'}
          onClose={() => setResult(null)}
          withCloseButton
        >
          <Text>{result.message}</Text>
          {result.data && (
            <Paper p="sm" mt="xs" bg="gray.0">
              <Text size="xs" ff="monospace">
                {JSON.stringify(result.data, null, 2)}
              </Text>
            </Paper>
          )}
        </Alert>
      )}
    </Stack>
  );
};

export default memo(TradingPanel);

// API Configuration
const getBaseUrl = () => {
  if (typeof process !== 'undefined' && (process as any).env?.VITE_API_URL) {
    return (process as any).env.VITE_API_URL as string;
  }
  return 'http://localhost:8001';
};

export const API_CONFIG = {
  // W ≈õrodowisku deweloperskim u≈ºywaj localhost, w produkcji HTTPS
  BASE_URL: getBaseUrl(),

  // Endpoints
  ENDPOINTS: {
    BOT_CONFIG: '/bot/config',
    BOT_STRATEGIES: '/bot/strategies',
    BOT_START: '/bot/start',
    BOT_STOP: '/bot/stop',
    BOT_STATUS: '/bot/status',
    ACCOUNT_INFO: '/account/info',
    MARKET_DATA: '/market/data',
  }
};

// Helper function to build full URL
export const buildApiUrl = (endpoint: string): string => {
  return `${API_CONFIG.BASE_URL}${endpoint}`;
};

// Secure fetch wrapper
export const secureApiCall = async (endpoint: string, options?: RequestInit) => {
  const url = buildApiUrl(endpoint);

  // W produkcji, sprawd≈∫ czy URL jest HTTPS
  const isProd = (typeof process !== 'undefined' && (process as any).env?.NODE_ENV === 'production') || false;

  if (isProd && !url.startsWith('https://')) {
    throw new Error('Production requires HTTPS connections');
  }

  return fetch(url, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options?.headers,
    },
  });
};

import { useEffect, useState } from 'react';
import { fetchAllTradingPairs } from '../services/binanceAPI';
import BinanceTickerWSClient from '../services/BinanceTickerWSClient';
import type { BinanceTicker24hr } from '../services/BinanceTickerWSClient';
import type { Asset } from '../types/asset';

export interface UseAssetsReturn {
  assets: Asset[];
  loading: boolean;
  error: string | null;
  refetch: () => void;
  isConnected: boolean;
  setPreferredQuotes: (quotes: string[] | null) => void;
}

type AssetsState = {
  assets: Asset[];
  loading: boolean;
  error: string | null;
  isConnected: boolean;
};

const MAX_SUBSCRIPTIONS = Number((typeof process !== 'undefined' && (process as any).env?.VITE_MAX_TICKER_SUBS) || 100);
const MARKET_QUOTES: string[] = (((typeof process !== 'undefined' && (process as any).env?.VITE_MARKET_QUOTES) || 'USDT,BTC,ETH,BNB'))
  .split(',')
  .map((q: string) => q.trim().toUpperCase())
  .filter(Boolean);
const FETCH_COOLDOWN = 60000; // 60s
const UPDATE_THROTTLE_MS = 750; // Increased from 500ms to 750ms for better performance

class AssetStore {
  state: AssetsState = { assets: [], loading: true, error: null, isConnected: false };
  subscribers = new Set<(s: AssetsState) => void>();
  initialized = false;
  wsClient: BinanceTickerWSClient | null = null;
  connectionInterval: number | null = null;
  refreshInterval: number | null = null;
  lastFetch = 0;
  pendingTickers = new Map<string, BinanceTicker24hr>();
  throttleTimer: number | null = null;
  preferredQuotes: string[] | null = null; // when set, prioritize these quotes for subscriptions

  init() {
    if (this.initialized) return;
    this.initialized = true;
    this.fetchAssets(true);

    // Fallback periodic refresh if WS not connected
    this.refreshInterval = window.setInterval(() => {
      const connected = this.wsClient?.isConnected ?? false;
      if (!connected) this.fetchAssets(false);
    }, 120000);

    window.addEventListener('beforeunload', () => this.destroy());
  }

  destroy() {
    if (this.connectionInterval) {
      clearInterval(this.connectionInterval);
      this.connectionInterval = null;
    }
    if (this.refreshInterval) {
      clearInterval(this.refreshInterval);
      this.refreshInterval = null;
    }
    if (this.throttleTimer) {
      clearTimeout(this.throttleTimer);
      this.throttleTimer = null;
    }
    if (this.wsClient) {
      this.wsClient.destroy();
      this.wsClient = null;
    }
    this.subscribers.clear();
    this.initialized = false;
  }

  notify() {
    for (const cb of this.subscribers) cb(this.state);
  }

  async fetchAssets(force = false) {
    const now = Date.now();
    if (!force && now - this.lastFetch < FETCH_COOLDOWN) return;

    try {
      this.state = { ...this.state, loading: true, error: null };
      this.notify();
      const data = await fetchAllTradingPairs();
      this.state = { ...this.state, assets: data, loading: false };
      this.lastFetch = now;
      this.notify();
      if (!this.wsClient) this.initWS();
      else this.updateSubscriptions();
    } catch (e) {
      const msg = e instanceof Error ? e.message : 'Failed to fetch assets';
      this.state = { ...this.state, error: msg, loading: false };
      this.notify();
    }
  }

  initWS() {
    if (this.wsClient) return;
    this.wsClient = new BinanceTickerWSClient();
    this.wsClient.addListener((tickers) => this.onTickers(tickers));
    // Connection monitor
    this.connectionInterval = window.setInterval(() => {
      const connected = this.wsClient?.isConnected ?? false;
      if (connected !== this.state.isConnected) {
        this.state = { ...this.state, isConnected: connected };
        this.notify();
      }
    }, 5000);
    this.updateSubscriptions();
  }

  updateSubscriptions() {
    if (!this.wsClient || this.state.assets.length === 0) return;
    const allowedQuotes = (this.preferredQuotes && this.preferredQuotes.length > 0)
      ? this.preferredQuotes.map(q => q.toUpperCase())
      : MARKET_QUOTES;
    const candidates = this.state.assets.filter(a => allowedQuotes.includes(a.quoteAsset));

    // r√≥wny przydzia≈Ç subskrypcji per rynek, aby nie faworyzowaƒá tylko USDT
    const perQuote = Math.max(1, Math.floor(MAX_SUBSCRIPTIONS / Math.max(1, allowedQuotes.length)));
    const byQuote = new Map<string, Asset[]>();
    for (const q of allowedQuotes) byQuote.set(q, []);
    for (const a of candidates) byQuote.get(a.quoteAsset)?.push(a);
  for (const [_q, arr] of byQuote) arr.sort((a, b) => b.volume - a.volume);

    const picked: Asset[] = [];
    for (const q of allowedQuotes) {
      const arr = byQuote.get(q) || [];
      picked.push(...arr.slice(0, perQuote));
    }

    // Je≈õli mamy jeszcze wolne sloty, dobij og√≥lnie wg wolumenu
    if (picked.length < MAX_SUBSCRIPTIONS) {
      const pickedSet = new Set(picked.map(a => a.symbol));
      const remaining = candidates
        .filter(a => !pickedSet.has(a.symbol))
        .sort((a, b) => b.volume - a.volume);
      picked.push(...remaining.slice(0, MAX_SUBSCRIPTIONS - picked.length));
    }

    const symbols = Array.from(new Set(picked.map(a => a.symbol)));
    this.wsClient.setSubscriptions(symbols);
  }

  setPreferredQuotes(quotes: string[] | null) {
    this.preferredQuotes = quotes && quotes.length ? quotes.map(q => q.toUpperCase()) : null;
    this.updateSubscriptions();
  }

  onTickers(tickers: BinanceTicker24hr[]) {
    for (const t of tickers) this.pendingTickers.set(t.s, t);
    if (this.throttleTimer === null) {
      this.throttleTimer = window.setTimeout(() => {
        this.throttleTimer = null;
        if (this.pendingTickers.size === 0) return;

        const updates = new Map(this.pendingTickers);
        this.pendingTickers.clear();

        // Optimized: update only changed assets, preserve references for unchanged ones
        const next = this.state.assets.map(a => {
          const t = updates.get(a.symbol);
          if (!t) return a; // Preserve reference for unchanged assets

          const newPrice = parseFloat(t.c);
          const newChangePercent = parseFloat(t.P);
          const newVolume = parseFloat(t.q);
          const newHighPrice = parseFloat(t.h);
          const newLowPrice = parseFloat(t.l);

          // Check if values actually changed to avoid unnecessary object creation
          if (a.price === newPrice && a.priceChangePercent === newChangePercent && 
              a.volume === newVolume && a.highPrice === newHighPrice && a.lowPrice === newLowPrice) {
            return a; // No change, preserve reference
          }

          return {
            ...a,
            price: newPrice,
            priceChangePercent: newChangePercent,
            volume: newVolume,
            highPrice: newHighPrice,
            lowPrice: newLowPrice,
          } as Asset;
        });
        this.state = { ...this.state, assets: next };
        this.notify();
      }, UPDATE_THROTTLE_MS);
    }
  }
}

const store = new AssetStore();

export const useAssets = (): UseAssetsReturn => {
  const [state, setState] = useState<AssetsState>(store.state);

  useEffect(() => {
    store.init();
    setState(store.state);
    const cb = (s: AssetsState) => setState(s);
    store.subscribers.add(cb);
    return () => {
      store.subscribers.delete(cb);
      // Don't destroy global store on unmount of a single consumer
    };
  }, []);

  return {
    assets: state.assets,
    loading: state.loading,
    error: state.error,
    refetch: () => store.fetchAssets(true),
    isConnected: state.isConnected,
  setPreferredQuotes: (quotes: string[] | null) => store.setPreferredQuotes(quotes),
  };
};

import React, { useRef, useEffect, useCallback } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  LineElement,
  LineController,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import type { ChartData, ChartOptions } from 'chart.js';
import 'chartjs-adapter-date-fns';

// Zarejestruj komponenty Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  TimeScale,
  PointElement,
  LineElement,
  LineController,
  Title,
  Tooltip,
  Legend,
  Filler
);

console.log('[useChart] Chart.js components registered successfully');

// Type definitions for better type safety
export type ChartUpdateMode = 'default' | 'none' | 'resize' | 'reset' | 'show' | 'hide' | 'active';

export type ChartDataValue = number | { x: number; y: number } | [number, number] | null;

export interface SafeChartOptions extends ChartOptions {
  // Add any specific options we need
}

export interface ChartConfiguration {
  type: 'line' | 'bar' | 'pie' | 'doughnut' | 'polarArea' | 'radar' | 'scatter' | 'bubble';
  data: ChartData;
  options?: ChartOptions;
}

// Safety validation functions
const validateDatasetIndex = (index: number, datasets: unknown[]): boolean => {
  return Number.isInteger(index) && index >= 0 && index < datasets.length;
};

const isValidChartData = (data: unknown): data is ChartData => {
  return typeof data === 'object' && data !== null && 'datasets' in data;
};

/**
 * Custom hook for managing Chart.js instances with proper lifecycle management
 * 
 * Features:
 * - Automatic cleanup of previous chart instances
 * - Type-safe Chart.js configuration
 * - Memory leak prevention
 * - Error handling for canvas operations
 * 
 * @param config - Chart.js configuration object
 * @param dependencies - Array of dependencies that trigger chart recreation
 * @returns Object containing chartRef for canvas element and chart instance
 */
export function useChart(
  config: ChartConfiguration,
  dependencies: React.DependencyList = []
) {
  const chartRef = useRef<HTMLCanvasElement>(null);
  const chartInstanceRef = useRef<ChartJS | null>(null);
  const canvasIdRef = useRef<string>(`chart-${Math.random().toString(36).substr(2, 9)}`);

  useEffect(() => {
    if (!chartRef.current) {
      console.warn('[useChart] Canvas ref is not available');
      return;
    }

    console.log('[useChart] Creating new Chart.js instance');

    // Always destroy existing chart first and clear canvas completely
    if (chartInstanceRef.current) {
      console.log('[useChart] Destroying existing chart instance');
      try {
        chartInstanceRef.current.destroy();
      } catch (error) {
        console.warn('[useChart] Error destroying existing chart:', error);
      }
      chartInstanceRef.current = null;
    }

    // Ensure canvas has unique ID and clear it
    const canvas = chartRef.current;
    canvas.id = canvasIdRef.current;
    const ctx = canvas.getContext('2d');
    if (ctx) {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // Reset canvas size to trigger complete cleanup
      canvas.width = canvas.offsetWidth;
      canvas.height = canvas.offsetHeight;
    }

    try {
      // Safe type assertion - ChartConfiguration is compatible with Chart.js config
      chartInstanceRef.current = new ChartJS(canvas, config);
      console.log('[useChart] Chart.js instance created successfully');
    } catch (error) {
      console.error('[useChart] Error creating chart:', error);
    }

    return () => {
      if (chartInstanceRef.current) {
        console.log('[useChart] Cleanup: destroying chart instance');
        try {
          chartInstanceRef.current.destroy();
        } catch (error) {
          console.warn('[useChart] Failed to destroy chart instance:', error);
        } finally {
          chartInstanceRef.current = null;
        }
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [config, ...dependencies]);

  // Additional cleanup on unmount
  useEffect(() => {
    return () => {
      if (chartInstanceRef.current) {
        try {
          chartInstanceRef.current.destroy();
        } catch (error) {
          console.warn('[useChart] Failed to destroy chart instance during unmount:', error);
        } finally {
          chartInstanceRef.current = null;
        }
      }
    };
  }, []);

  const updateChart = useCallback((data?: ChartData, options: ChartUpdateMode = 'default') => {
    if (chartInstanceRef.current) {
      if (data && isValidChartData(data)) {
        console.log('[useChart] Updating chart data');
        chartInstanceRef.current.data = data;
      }
      chartInstanceRef.current.update(options);
    }
  }, []);

  const updateDataset = useCallback((datasetIndex: number, newData: ChartDataValue[]) => {
    if (chartInstanceRef.current && chartInstanceRef.current.data.datasets) {
      if (!validateDatasetIndex(datasetIndex, chartInstanceRef.current.data.datasets)) {
        console.error(`[useChart] Invalid dataset index: ${datasetIndex}`);
        return;
      }

      console.log(`[useChart] Updating dataset ${datasetIndex} with ${newData.length} points`);
      chartInstanceRef.current.data.datasets[datasetIndex].data = newData;
      chartInstanceRef.current.update();
    }
  }, []);

  const addDataPoint = useCallback((label: Date | string | number, datasetIndex: number, value: number, maxPoints = 100) => {
    console.log(`[useChart] Adding data point: label=${String(label)}, value=${value}, datasetIndex=${datasetIndex}`);

    if (!chartInstanceRef.current || !chartInstanceRef.current.data.datasets) {
      console.error('[useChart] Chart instance or datasets not available');
      return;
    }

    if (!validateDatasetIndex(datasetIndex, chartInstanceRef.current.data.datasets)) {
      console.error(`[useChart] Invalid dataset index: ${datasetIndex}`);
      return;
    }

    const chart = chartInstanceRef.current;
    const dataset = chart.data.datasets[datasetIndex];

    if (!dataset) {
      console.error(`[useChart] Dataset at index ${datasetIndex} not found`);
      return;
    }

    // Safely add new data
    if (!chart.data.labels) {
      chart.data.labels = [];
    }
    if (!Array.isArray(dataset.data)) {
      dataset.data = [];
    }

    chart.data.labels.push(label);
    (dataset.data as ChartDataValue[]).push(value);

    console.log(`[useChart] Chart data after adding: labels=${chart.data.labels.length}, data=${dataset.data.length}`);

    // Remove oldest data if exceeding maxPoints
    if (chart.data.labels.length > maxPoints) {
      chart.data.labels.shift();
      (dataset.data as ChartDataValue[]).shift();
      console.log(`[useChart] Removed old data point, current length: ${chart.data.labels.length}`);
    }

    chart.update('none');
    console.log('[useChart] Chart updated successfully');
  }, []);

  const clearChart = useCallback(() => {
    if (chartInstanceRef.current) {
      console.log('[useChart] Clearing chart data');
      chartInstanceRef.current.data.labels = [];
      chartInstanceRef.current.data.datasets.forEach(dataset => {
        dataset.data = [];
      });
      chartInstanceRef.current.update();
    }
  }, []);

  return {
    chartRef,
    chartInstance: chartInstanceRef.current,
    updateChart,
    updateDataset,
    addDataPoint,
    clearChart
  };
}

/**
 * Type definitions for better TypeScript support
 */
export interface ChartHookReturn {
  chartRef: React.RefObject<HTMLCanvasElement>;
  chartInstance: ChartJS | null;
  updateChart: (data?: ChartData, options?: ChartUpdateMode) => void;
  updateDataset: (datasetIndex: number, newData: ChartDataValue[]) => void;
  addDataPoint: (label: Date | string | number, datasetIndex: number, value: number, maxPoints?: number) => void;
  clearChart: () => void;
}

export default useChart;

import { useRef, useCallback, useState } from 'react';
import { LineSeries } from 'lightweight-charts';
import type { IChartApi, ISeriesApi, LineData } from 'lightweight-charts';
import type { CandleData } from '../indicators';
import {
  calculateRSI,
  calculateMovingAverage,
  calculateMACD,
  calculateBollingerBands,
  DEFAULT_RSI_CONFIG,
  DEFAULT_MA_CONFIG,
  DEFAULT_MACD_CONFIG,
  DEFAULT_BOLLINGER_BANDS_CONFIG,
  type RSIConfig,
  type MovingAverageConfig,
  type MACDConfig,
  type BollingerBandsConfig
} from '../indicators';

export interface IndicatorSeries {
  id: string;
  type: 'RSI' | 'MA' | 'MACD' | 'BB';
  name: string;
  series: ISeriesApi<'Line'>[];
  visible: boolean;
  config: any;
}

/**
 * Hook for managing technical indicators on charts
 */
export function useChartIndicators(chartInstance: IChartApi | null) {
  const [indicators, setIndicators] = useState<IndicatorSeries[]>([]);
  const indicatorsRef = useRef<IndicatorSeries[]>([]);

  // Update ref when indicators change
  useCallback(() => {
    indicatorsRef.current = indicators;
  }, [indicators]);

  // Convert CandlestickData to CandleData format
  const convertToIndicatorData = useCallback((data: any[]): CandleData[] => {
    return data.map(item => ({
      time: typeof item.time === 'number' ? item.time : new Date(item.time).getTime() / 1000,
      open: typeof item.open === 'number' ? item.open : parseFloat(item.open),
      high: typeof item.high === 'number' ? item.high : parseFloat(item.high),
      low: typeof item.low === 'number' ? item.low : parseFloat(item.low),
      close: typeof item.close === 'number' ? item.close : parseFloat(item.close),
      volume: item.volume ? (typeof item.volume === 'number' ? item.volume : parseFloat(item.volume)) : undefined
    }));
  }, []);

  // Add RSI indicator
  const addRSI = useCallback((data: any[], config: RSIConfig = DEFAULT_RSI_CONFIG) => {
    if (!chartInstance) return null;

    const indicatorData = convertToIndicatorData(data);
    const rsiValues = calculateRSI(indicatorData, config);

    const rsiSeries = chartInstance.addSeries(LineSeries, {
      color: '#FF6B35',
      lineWidth: 2,
      title: `RSI(${config.period})`,
      priceScaleId: 'rsi',
    });

    // Convert to LineData format
    const lineData: LineData[] = rsiValues.map(value => ({
      time: value.time as any,
      value: value.value
    }));

    rsiSeries.setData(lineData);

    // Add horizontal lines for overbought/oversold levels
    const overboughtSeries = chartInstance.addSeries(LineSeries, {
      color: '#FF4444',
      lineWidth: 1,
      lineStyle: 2, // dashed
      priceScaleId: 'rsi',
    });

    const oversoldSeries = chartInstance.addSeries(LineSeries, {
      color: '#44FF44',
      lineWidth: 1,
      lineStyle: 2, // dashed
      priceScaleId: 'rsi',
    });

    // Create horizontal line data
    if (rsiValues.length > 0) {
      const startTime = rsiValues[0].time;
      const endTime = rsiValues[rsiValues.length - 1].time;

      overboughtSeries.setData([
        { time: startTime as any, value: config.overbought },
        { time: endTime as any, value: config.overbought }
      ]);

      oversoldSeries.setData([
        { time: startTime as any, value: config.oversold },
        { time: endTime as any, value: config.oversold }
      ]);
    }

    const indicator: IndicatorSeries = {
      id: `rsi-${Date.now()}`,
      type: 'RSI',
      name: `RSI(${config.period})`,
      series: [rsiSeries, overboughtSeries, oversoldSeries],
      visible: true,
      config
    };

    setIndicators(prev => [...prev, indicator]);
    return indicator.id;
  }, [chartInstance, convertToIndicatorData]);

  // Add Moving Average indicator
  const addMovingAverage = useCallback((data: any[], config: MovingAverageConfig = DEFAULT_MA_CONFIG) => {
    if (!chartInstance) return null;

    const indicatorData = convertToIndicatorData(data);
    const maValues = calculateMovingAverage(indicatorData, config);

    const maSeries = chartInstance.addSeries(LineSeries, {
      color: config.type === 'SMA' ? '#2962FF' : '#FF9800',
      lineWidth: 2,
      title: `${config.type}(${config.period})`,
    });

    const lineData: LineData[] = maValues.map(value => ({
      time: value.time as any,
      value: value.value
    }));

    maSeries.setData(lineData);

    const indicator: IndicatorSeries = {
      id: `ma-${Date.now()}`,
      type: 'MA',
      name: `${config.type}(${config.period})`,
      series: [maSeries],
      visible: true,
      config
    };

    setIndicators(prev => [...prev, indicator]);
    return indicator.id;
  }, [chartInstance, convertToIndicatorData]);

  // Add MACD indicator
  const addMACD = useCallback((data: any[], config: MACDConfig = DEFAULT_MACD_CONFIG) => {
    if (!chartInstance) return null;

    const indicatorData = convertToIndicatorData(data);
    const macdValues = calculateMACD(indicatorData, config);

    const macdSeries = chartInstance.addSeries(LineSeries, {
      color: '#2196F3',
      lineWidth: 2,
      title: `MACD(${config.fastPeriod},${config.slowPeriod},${config.signalPeriod})`,
      priceScaleId: 'macd',
    });

    const signalSeries = chartInstance.addSeries(LineSeries, {
      color: '#FF5722',
      lineWidth: 2,
      title: 'Signal',
      priceScaleId: 'macd',
    });

    const macdLineData: LineData[] = macdValues.map(value => ({
      time: value.time as any,
      value: value.macd
    }));

    const signalLineData: LineData[] = macdValues.map(value => ({
      time: value.time as any,
      value: value.signal
    }));

    macdSeries.setData(macdLineData);
    signalSeries.setData(signalLineData);

    const indicator: IndicatorSeries = {
      id: `macd-${Date.now()}`,
      type: 'MACD',
      name: `MACD(${config.fastPeriod},${config.slowPeriod},${config.signalPeriod})`,
      series: [macdSeries, signalSeries],
      visible: true,
      config
    };

    setIndicators(prev => [...prev, indicator]);
    return indicator.id;
  }, [chartInstance, convertToIndicatorData]);

  // Add Bollinger Bands indicator
  const addBollingerBands = useCallback((data: any[], config: BollingerBandsConfig = DEFAULT_BOLLINGER_BANDS_CONFIG) => {
    if (!chartInstance) return null;

    const indicatorData = convertToIndicatorData(data);
    const bbValues = calculateBollingerBands(indicatorData, config);

    const upperSeries = chartInstance.addSeries(LineSeries, {
      color: '#9C27B0',
      lineWidth: 1,
      title: `BB Upper(${config.period})`,
    });

    const middleSeries = chartInstance.addSeries(LineSeries, {
      color: '#673AB7',
      lineWidth: 2,
      title: `BB Middle(${config.period})`,
    });

    const lowerSeries = chartInstance.addSeries(LineSeries, {
      color: '#9C27B0',
      lineWidth: 1,
      title: `BB Lower(${config.period})`,
    });

    const upperData: LineData[] = bbValues.map(value => ({
      time: value.time as any,
      value: value.upper
    }));

    const middleData: LineData[] = bbValues.map(value => ({
      time: value.time as any,
      value: value.middle
    }));

    const lowerData: LineData[] = bbValues.map(value => ({
      time: value.time as any,
      value: value.lower
    }));

    upperSeries.setData(upperData);
    middleSeries.setData(middleData);
    lowerSeries.setData(lowerData);

    const indicator: IndicatorSeries = {
      id: `bb-${Date.now()}`,
      type: 'BB',
      name: `BB(${config.period})`,
      series: [upperSeries, middleSeries, lowerSeries],
      visible: true,
      config
    };

    setIndicators(prev => [...prev, indicator]);
    return indicator.id;
  }, [chartInstance, convertToIndicatorData]);

  // Remove indicator
  const removeIndicator = useCallback((indicatorId: string) => {
    if (!chartInstance) return;

    const indicator = indicators.find(ind => ind.id === indicatorId);
    if (indicator) {
      indicator.series.forEach(series => {
        chartInstance.removeSeries(series);
      });

      setIndicators(prev => prev.filter(ind => ind.id !== indicatorId));
    }
  }, [chartInstance, indicators]);

  // Toggle indicator visibility
  const toggleIndicator = useCallback((indicatorId: string) => {
    setIndicators(prev => prev.map(indicator => {
      if (indicator.id === indicatorId) {
        const newVisibility = !indicator.visible;
        indicator.series.forEach(series => {
          series.applyOptions({
            visible: newVisibility
          });
        });
        return { ...indicator, visible: newVisibility };
      }
      return indicator;
    }));
  }, []);

  // Clear all indicators
  const clearAllIndicators = useCallback(() => {
    if (!chartInstance) return;

    indicators.forEach(indicator => {
      indicator.series.forEach(series => {
        chartInstance.removeSeries(series);
      });
    });

    setIndicators([]);
  }, [chartInstance, indicators]);

  return {
    indicators,
    addRSI,
    addMovingAverage,
    addMACD,
    addBollingerBands,
    removeIndicator,
    toggleIndicator,
    clearAllIndicators
  };
}

import { useState, useEffect, useCallback, useMemo } from 'react';
import { getAccount } from '../services/restClient';
import type { AccountResponse, Balance } from '../services/restClient';
import type { PortfolioBalance, MiCAComplianceInfo, MiCAComplianceStatus } from '../types/portfolio';
import { useAssets } from './useAssets';
import { useThrottledState } from './useThrottledState';

export interface UsePortfolioReturn {
  balances: PortfolioBalance[];
  loading: boolean;
  error: string | null;
  accountData: AccountResponse | null;
  refetch: () => Promise<void>;
  totalValue: number;
  totalChange24h: number;
  isConnected: boolean;
  lastSyncTime: number | null;
}

/**
 * Hook do zarzƒÖdzania danymi portfolio u≈ºytkownika
 * ≈ÅƒÖczy dane z AccountPanel z cenami z MarketPanel
 */
export const usePortfolio = (): UsePortfolioReturn => {
  const [accountData, setAccountData] = useState<AccountResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastSyncTime, setLastSyncTime] = useState<number | null>(null);

  // Use the existing assets hook for market data
  const { assets: marketData, isConnected } = useAssets();

  // MiCA compliance mapping for EU regulations
  const getMiCAComplianceStatus = useCallback((asset: string): MiCAComplianceInfo => {
    // MiCA-compliant stablecoins (remain available in EU)
    const MICA_COMPLIANT = new Set(['USDC']);

    // Non-compliant stablecoins (delisted from EU by March 31, 2025)
    const EU_DELISTING = new Set(['USDT', 'FDUSD', 'TUSD', 'USDP', 'DAI', 'AEUR', 'XUSD', 'PAXG']);

    if (MICA_COMPLIANT.has(asset)) {
      return {
        status: 'COMPLIANT',
        recommendation: 'MiCA-compliant, pozostanie dostƒôpny w EU'
      };
    }

    if (EU_DELISTING.has(asset)) {
      return {
        status: 'DELISTING',
        delistingDate: '31 marca 2025',
        recommendation: 'Zostanie usuniƒôty z Binance EU. Rozwa≈º konwersjƒô do USDC'
      };
    }

    return {
      status: 'UNKNOWN',
      recommendation: 'Status MiCA nieznany'
    };
  }, []);

  const fetchAccountData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getAccount();
      setAccountData(data);
      setLastSyncTime(Date.now());
    } catch (err) {
      setError(err instanceof Error ? err.message : 'B≈ÇƒÖd podczas pobierania danych portfolio');
    } finally {
      setLoading(false);
    }
  }, []);

  // Initial fetch
  useEffect(() => {
    fetchAccountData();
  }, [fetchAccountData]);

  // Fiat currencies that need special handling (inverted USD pairs)
  const FIAT_CURRENCIES = useMemo(() => new Set([
    'EUR', 'GBP', 'PLN', 'JPY', 'CNY', 'TRY', 'BRL', 'ARS', 'MXN', 'ZAR', 'UAH', 'RON',
    'KZT', 'NGN', 'CZK', 'CHF', 'SEK', 'NOK', 'DKK', 'HUF', 'AUD', 'NZD', 'CAD',
    'HKD', 'SGD', 'COP', 'CLP', 'PEN', 'PHP', 'IDR', 'INR', 'THB', 'VND', 'ILS',
    'AED', 'SAR', 'QAR', 'KRW', 'MYR'
  ]), []);

  // Helper function to find market data for any asset
  const findMarketPrice = useCallback((asset: string) => {
    // USDC is our new base currency (MiCA-compliant)
    if (asset === 'USDC') {
      return { price: 1, priceChangePercent: 0 };
    }

    // Major stablecoins - assume ~$1.00 for portfolio tracking
    // (exact spreads are not critical for portfolio valuation)
    const MAJOR_STABLECOINS = new Set(['USDT', 'DAI', 'TUSD', 'USDP', 'FDUSD']);
    if (MAJOR_STABLECOINS.has(asset)) {
      return { price: 1, priceChangePercent: 0 };
    }

    // First try standard format: {ASSET}USDC
    let marketAsset = marketData.find(m => m.symbol === `${asset}USDC`);
    if (marketAsset) {
      return { price: marketAsset.price, priceChangePercent: marketAsset.priceChangePercent };
    }

    // For fiat currencies, try different USDC-based formats (MiCA-compliant)
    if (FIAT_CURRENCIES.has(asset)) {
      // Try USDC{FIAT} format (e.g., USDCPLN, USDCTRY)
      marketAsset = marketData.find(m => m.symbol === `USDC${asset}`);
      if (marketAsset && marketAsset.price > 0) {
        // Invert the price: if USDCPLN = 4.0, then PLN price = 1/4.0 = 0.25 USD
        const invertedPrice = 1 / marketAsset.price;
        // For inverted pairs, we also need to invert the percentage change
        const invertedPriceChange = marketAsset.priceChangePercent ? -marketAsset.priceChangePercent : 0;
        return { price: invertedPrice, priceChangePercent: invertedPriceChange };
      }

      // Try {FIAT}USDC format (e.g., EURUSDC)
      marketAsset = marketData.find(m => m.symbol === `${asset}USDC`);
      if (marketAsset) {
        // Direct price: EURUSDC = 1.16 means 1 EUR = 1.16 USD
        return { price: marketAsset.price, priceChangePercent: marketAsset.priceChangePercent };
      }

      // Fallback for fiat: Try USDT{FIAT} format (e.g., USDTZAR, USDTUAH)
      marketAsset = marketData.find(m => m.symbol === `USDT${asset}`);
      if (marketAsset && marketAsset.price > 0) {
        // Invert the price: if USDTZAR = 18.0, then ZAR price = 1/18.0 = 0.056 USD
        const invertedPrice = 1 / marketAsset.price;
        // For inverted pairs, we also need to invert the percentage change
        const invertedPriceChange = marketAsset.priceChangePercent ? -marketAsset.priceChangePercent : 0;
        return { price: invertedPrice, priceChangePercent: invertedPriceChange };
      }

      // Fallback for fiat: Try {FIAT}USDT format (e.g., EURUSDT)
      marketAsset = marketData.find(m => m.symbol === `${asset}USDT`);
      if (marketAsset) {
        // Direct price: EURUSDT = 1.16 means 1 EUR = 1.16 USD
        return { price: marketAsset.price, priceChangePercent: marketAsset.priceChangePercent };
      }
    }

    // Fallback: Try {ASSET}USDT for assets that don't have USDC pairs yet
    // Note: This will be phased out as USDT pairs are delisted in EU
    marketAsset = marketData.find(m => m.symbol === `${asset}USDT`);
    if (marketAsset) {
      return { price: marketAsset.price, priceChangePercent: marketAsset.priceChangePercent };
    }

    return { price: undefined, priceChangePercent: undefined };
  }, [marketData, FIAT_CURRENCIES]);

  // Transform account balances to portfolio format with market data
  const balances: PortfolioBalance[] = useMemo(() => {
    if (!accountData?.balances || !Array.isArray(accountData.balances)) {
      return [];
    }

    return accountData.balances.map((balance: Balance) => {
      const asset = balance.asset;
      const free = parseFloat(balance.free);
      const locked = parseFloat(balance.locked);
      const total = free + locked;

      // Find market data for this asset (handles both crypto and fiat)
      const marketPrice = findMarketPrice(asset);
      const currentPrice = marketPrice.price;
      const priceChange24h = marketPrice.priceChangePercent;

      // Calculate USD values
      const valueUSD = currentPrice ? total * currentPrice : 0;
      const valueChange24h = currentPrice && priceChange24h 
        ? valueUSD * (priceChange24h / 100) 
        : 0;

      return {
        asset,
        free,
        locked,
        total,
        currentPrice,
        priceChange24h,
        valueUSD,
        valueChange24h,
        micaCompliance: getMiCAComplianceStatus(asset),
      };
    });
  }, [accountData?.balances, marketData, getMiCAComplianceStatus]);

  // Calculate portfolio metrics with throttling
  const totalValue = useMemo(() => {
    return balances.reduce((total, balance) => total + (balance.valueUSD || 0), 0);
  }, [balances]);

  // Use throttled state for total value to prevent excessive UI updates
  const [throttledTotalValue, setThrottledTotalValue] = useThrottledState(totalValue, 500);

  // Update throttled value when totalValue changes
  useEffect(() => {
    setThrottledTotalValue(totalValue);
  }, [totalValue, setThrottledTotalValue]);

  const totalChange24h = useMemo(() => {
    const totalCurrent = totalValue;
    const totalPrevious = balances.reduce((total, balance) => {
      const previousValue = (balance.valueUSD || 0) - (balance.valueChange24h || 0);
      return total + previousValue;
    }, 0);

    if (totalPrevious === 0) return 0;
    return ((totalCurrent - totalPrevious) / totalPrevious) * 100;
  }, [balances, totalValue]);

  return {
    balances,
    loading,
    error,
    accountData,
    refetch: fetchAccountData,
    totalValue: throttledTotalValue,
    totalChange24h,
    isConnected,
    lastSyncTime,
  };
};

import { useRef, useEffect } from 'react';

/**
 * Hook do wykrywania zmian w cenie aktyw√≥w dla animacji
 * @param assets - lista aktyw√≥w do monitorowania
 * @returns Map z symbolami i kierunkiem zmiany ('up' | 'down' | null)
 */
export const usePriceChangeAnimation = (assets: any[]) => {
  const previousPricesRef = useRef<Map<string, number>>(new Map());
  const changesRef = useRef<Map<string, 'up' | 'down' | null>>(new Map());

  useEffect(() => {
    const currentPrices = new Map<string, number>();
    const newChanges = new Map<string, 'up' | 'down' | null>();

    assets.forEach(asset => {
      const currentPrice = asset.price;
      const previousPrice = previousPricesRef.current.get(asset.symbol);

      currentPrices.set(asset.symbol, currentPrice);

      if (previousPrice !== undefined && previousPrice !== currentPrice) {
        if (currentPrice > previousPrice) {
          newChanges.set(asset.symbol, 'up');
        } else if (currentPrice < previousPrice) {
          newChanges.set(asset.symbol, 'down');
        }
      }
    });

    // Update refs
    previousPricesRef.current = currentPrices;
    changesRef.current = newChanges;

    // Clear animations after 2 seconds
    if (newChanges.size > 0) {
      setTimeout(() => {
        changesRef.current.clear();
      }, 2000);
    }
  }, [assets]);

  return changesRef.current;
};

import { useRef, useCallback } from 'react';

/**
 * Custom hook for throttling state updates in high-frequency scenarios
 * Uses requestAnimationFrame for smooth performance
 */
export function useThrottledCallback<T extends (...args: any[]) => void>(
  callback: T,
  delay: number = 16 // ~60fps
): T {
  const timeoutRef = useRef<number | null>(null);
  const lastCallRef = useRef<number>(0);

  const throttledCallback = useCallback((...args: Parameters<T>) => {
    const now = performance.now();

    if (now - lastCallRef.current >= delay) {
      // Call immediately if enough time has passed
      lastCallRef.current = now;
      callback(...args);
    } else {
      // Schedule for later if called too frequently
      if (timeoutRef.current) {
        cancelAnimationFrame(timeoutRef.current);
      }

      timeoutRef.current = requestAnimationFrame(() => {
        lastCallRef.current = performance.now();
        callback(...args);
        timeoutRef.current = null;
      });
    }
  }, [callback, delay]) as T;

  return throttledCallback;
}

import type { CandleData, RSIValue, RSIConfig } from './types';

/**
 * Calculate RSI (Relative Strength Index)
 * @param data Array of candle data
 * @param config RSI configuration
 * @returns Array of RSI values
 */
export function calculateRSI(data: CandleData[], config: RSIConfig): RSIValue[] {
  const { period } = config;
  const result: RSIValue[] = [];

  if (data.length < period + 1) {
    return result;
  }

  let gains: number[] = [];
  let losses: number[] = [];

  // Calculate initial gains and losses
  for (let i = 1; i <= period; i++) {
    const change = data[i].close - data[i - 1].close;
    gains.push(change > 0 ? change : 0);
    losses.push(change < 0 ? Math.abs(change) : 0);
  }

  let avgGain = gains.reduce((sum, gain) => sum + gain, 0) / period;
  let avgLoss = losses.reduce((sum, loss) => sum + loss, 0) / period;

  // Calculate RSI for the first period
  let rs = avgGain / (avgLoss || 1);
  let rsi = 100 - (100 / (1 + rs));

  result.push({
    time: data[period].time,
    value: rsi
  });

  // Calculate subsequent RSI values using smoothed averages
  for (let i = period + 1; i < data.length; i++) {
    const change = data[i].close - data[i - 1].close;
    const gain = change > 0 ? change : 0;
    const loss = change < 0 ? Math.abs(change) : 0;

    avgGain = ((avgGain * (period - 1)) + gain) / period;
    avgLoss = ((avgLoss * (period - 1)) + loss) / period;

    rs = avgGain / (avgLoss || 1);
    rsi = 100 - (100 / (1 + rs));

    result.push({
      time: data[i].time,
      value: rsi
    });
  }

  return result;
}

