// Optimized Binance WebSocket Client for Selected Tickers
// Provides real-time 24hr ticker statistics for specific symbols only
// Fixes performance issue: was downloading 570MB for all symbols, now only selected ones

export interface BinanceTicker24hr {
  e: string;      // Event type (always "24hrTicker")
  E: number;      // Event time (timestamp in milliseconds)
  s: string;      // Symbol (e.g., "BTCUSDT")
  p: string;      // Price change
  P: string;      // Price change percent
  w: string;      // Weighted average price
  x: string;      // First trade(F)-1 price
  c: string;      // Last price
  Q: string;      // Last quantity
  b: string;      // Best bid price
  B: string;      // Best bid quantity
  a: string;      // Best ask price
  A: string;      // Best ask quantity
  o: string;      // Open price
  h: string;      // High price
  l: string;      // Low price
  v: string;      // Total traded base asset volume
  q: string;      // Total traded quote asset volume
  O: number;      // Statistics open time
  C: number;      // Statistics close time
  F: number;      // First trade ID
  L: number;      // Last trade Id
  n: number;      // Total number of trades
}

export type BinanceTickerListener = (tickers: BinanceTicker24hr[]) => void;

import { createLogger } from './logger';

export class BinanceTickerWSClient {
  private ws: WebSocket | null = null;
  private listeners: BinanceTickerListener[] = [];
  private shouldReconnect = true;
  private reconnectTimeout: number | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private isDestroyed = false;
  private readonly baseUrl: string;
  private subscribedSymbols = new Set<string>();

  private logger = createLogger('binance:ticker');

  constructor() {
    // Use environment variable for Binance WebSocket URL, with fallback
  // Prefer env var in tests; Vite will inline in builds
  // @ts-ignore
  const fromEnv = (typeof process !== 'undefined' && (process as any).env?.VITE_BINANCE_WS_URL) as string | undefined;
  this.baseUrl = fromEnv || 'wss://data-stream.binance.vision/ws';
  this.logger.debug('init', { baseUrl: this.baseUrl });
    // Don't auto-connect, wait for subscriptions
  }

  // debug flag handled by central logger now

  // Subscribe to specific symbols for ticker updates
  subscribe(symbols: string[]) {
    symbols.forEach(symbol => {
      const symbolLower = symbol.toLowerCase();
      if (!this.subscribedSymbols.has(symbolLower)) {
        this.subscribedSymbols.add(symbolLower);
  this.logger.trace('subscribe', { symbol });
      }
    });

    if (this.subscribedSymbols.size > 0 && !this.ws) {
      this.connect();
    }
  }

  // Unsubscribe from specific symbols
  unsubscribe(symbols: string[]) {
    symbols.forEach(symbol => {
      const symbolLower = symbol.toLowerCase();
      this.subscribedSymbols.delete(symbolLower);
  this.logger.trace('unsubscribe', { symbol });
    });

    if (this.subscribedSymbols.size === 0 && this.ws) {
  this.logger.debug('no-subscriptions-close');
      this.ws.close();
    }
  }

  private buildStreamUrl(): string {
    if (this.subscribedSymbols.size === 0) {
      throw new Error('No symbols subscribed');
    }

    // Build streams for subscribed symbols: symbol@ticker
    const streams = Array.from(this.subscribedSymbols).map(symbol => `${symbol}@ticker`);
    const streamParam = streams.join('/');

    // Support both single-stream (/ws/<stream>) and combined streams (/stream?streams=...)
    // If only one stream and baseUrl ends with /ws, use single stream path for efficiency
    if (streams.length === 1 && this.baseUrl.endsWith('/ws')) {
      return `${this.baseUrl}/${streams[0]}`;
    }

    // Otherwise use combined stream format regardless of trailing segment in baseUrl
    // Normalize to data-stream root, preserving potential custom host in baseUrl
    const root = this.baseUrl.replace(/\/ws$/i, '').replace(/\/stream\??streams=?$/i, '');
    return `${root}/stream?streams=${encodeURIComponent(streamParam)}`;
  }

  private connect() {
    if (this.isDestroyed) {
  this.logger.debug('skip-connect-destroyed');
      return;
    }

    if (this.subscribedSymbols.size === 0) {
  this.logger.trace('skip-connect-empty');
      return;
    }

  const url = this.buildStreamUrl();
    this.logger.debug('connecting', { url });

    try {
      this.ws = new WebSocket(url);

      this.ws.onopen = () => {
  this.logger.info('connected', { streams: this.subscribedSymbols.size });
        this.reconnectAttempts = 0;
      };

      this.ws.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);

          // Handle single ticker update (not array like before)
          if (data.e === '24hrTicker') {
            if (data.s && this.subscribedSymbols.has(String(data.s).toLowerCase())) {
              this.notifyListeners([data]); // Wrap in array for compatibility
            }
          } else if (Array.isArray(data)) {
            // Some endpoints may send arrays (safety)
            const filtered = (data as BinanceTicker24hr[]).filter(t => t.s && this.subscribedSymbols.has(String(t.s).toLowerCase()));
            if (filtered.length) this.notifyListeners(filtered);
          } else if (data && data.stream && data.data) {
            // Combined stream payload: { stream: 'btcusdt@ticker', data: {...} }
            const payload = data.data as BinanceTicker24hr;
            if (payload.e === '24hrTicker' && payload.s && this.subscribedSymbols.has(String(payload.s).toLowerCase())) {
              this.notifyListeners([payload]);
            }
          }
        } catch (error) {
          this.logger.warn('parse-failed', error);
        }
      };

      this.ws.onerror = (error) => {
  this.logger.error('ws-error', error);
  this.logger.debug('ws-error-meta', { url, state: this.ws?.readyState });
      };

      this.ws.onclose = (event) => {
  this.logger.warn('closed', { code: event.code, reason: event.reason });
        this.ws = null;

        // Don't attempt reconnection if the close was due to an invalid endpoint
        if (event.code === 1006) {
          this.logger.error('invalid-endpoint?');
          this.shouldReconnect = false;
          return;
        }

        if (this.shouldReconnect && !this.isDestroyed && this.reconnectAttempts < this.maxReconnectAttempts && this.subscribedSymbols.size > 0) {
          const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 30000);
          this.logger.debug('reconnect-schedule', { delay, attempt: this.reconnectAttempts + 1 });

          this.reconnectTimeout = window.setTimeout(() => {
            this.reconnectAttempts++;
            this.connect();
          }, delay);
        } else if (this.reconnectAttempts >= this.maxReconnectAttempts) {
          this.logger.error('reconnect-max');
        }
      };

    } catch (error) {
      console.error('[BinanceTickerWSClient] Failed to create WebSocket:', error);
    }
  }

  // Replace current subscriptions with a new set and reconnect
  setSubscriptions(symbols: string[]) {
    const next = new Set(symbols.map(s => s.toLowerCase()));
    // If identical, do nothing
    if (
      next.size === this.subscribedSymbols.size &&
      Array.from(next).every(s => this.subscribedSymbols.has(s))
    ) {
      return;
    }

  this.logger.debug('set-subscriptions', { count: next.size });
    this.subscribedSymbols = next;

    // Reconnect with new streams
    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }
    this.reconnectAttempts = 0;
    this.connect();
  }

  private notifyListeners(tickers: BinanceTicker24hr[]) {
    this.listeners.forEach(listener => {
      try {
        listener(tickers);
      } catch (error) {
  this.logger.error('listener-error', error);
      }
    });
  }

  addListener(listener: BinanceTickerListener) {
    this.listeners.push(listener);
  this.logger.trace('add-listener', { total: this.listeners.length });
  }

  removeListener(listener: BinanceTickerListener) {
    const index = this.listeners.indexOf(listener);
    if (index !== -1) {
      this.listeners.splice(index, 1);
  this.logger.trace('remove-listener', { total: this.listeners.length });
    }
  }

  destroy() {
  this.logger.info('destroy');
    this.isDestroyed = true;
    this.shouldReconnect = false;

    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }

    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }

    this.listeners = [];
  }

  // Get current connection state
  get isConnected(): boolean {
    return this.ws?.readyState === WebSocket.OPEN;
  }
}

export default BinanceTickerWSClient;

// Direct Binance REST API client for historical data
// This bypasses the backend and connects directly to Binance public API

import axios from 'axios';
import { createLogger } from './logger';
import type { Asset } from '../types/asset';

// Binance API base URL (public, no authentication required for market data)
const BINANCE_API_BASE = 'https://api.binance.com/api/v3';

export interface BinanceKlineData {
  openTime: number;        // Open time (timestamp in milliseconds)
  open: string;           // Open price
  high: string;           // High price
  low: string;            // Low price
  close: string;          // Close price
  volume: string;         // Volume
  closeTime: number;      // Close time (timestamp in milliseconds)
  quoteAssetVolume: string;
  numberOfTrades: number;
  takerBuyBaseAssetVolume: string;
  takerBuyQuoteAssetVolume: string;
}

// Raw response from Binance API (array format)
export type BinanceKlineRaw = [
  number,  // Open time
  string,  // Open price
  string,  // High price
  string,  // Low price
  string,  // Close price
  string,  // Volume
  number,  // Close time
  string,  // Quote asset volume
  number,  // Number of trades
  string,  // Taker buy base asset volume
  string   // Taker buy quote asset volume
];

// Lightweight-charts compatible format
export interface LightweightChartsKline {
  time: number;  // Unix timestamp in seconds (as Time type for lightweight-charts)
  open: number;
  high: number;
  low: number;
  close: number;
}

/**
 * Fetch historical klines directly from Binance API
 * @param symbol Trading pair symbol (e.g., 'BTCUSDT')
 * @param interval Kline interval (e.g., '1m', '5m', '1h', '1d')
 * @param limit Number of klines to retrieve (max 1000, default 100)
 * @returns Promise with klines data
 */
export async function fetchBinanceKlines(
  symbol: string, 
  interval: string = '1m', 
  limit: number = 100
): Promise<BinanceKlineData[]> {
  const logger = createLogger('binance:api');
  try {
    logger.debug('fetch-klines', { symbol, interval, limit });

    const response = await axios.get<BinanceKlineRaw[]>(`${BINANCE_API_BASE}/klines`, {
      params: {
        symbol: symbol.toUpperCase(),
        interval,
        limit: Math.min(limit, 1000) // Binance API limit
      },
      timeout: 10000
    });

    const klines: BinanceKlineData[] = response.data.map(raw => ({
      openTime: raw[0],
      open: raw[1],
      high: raw[2],
      low: raw[3],
      close: raw[4],
      volume: raw[5],
      closeTime: raw[6],
      quoteAssetVolume: raw[7],
      numberOfTrades: raw[8],
      takerBuyBaseAssetVolume: raw[9],
      takerBuyQuoteAssetVolume: raw[10]
    }));

  logger.trace('klines-fetched', klines.length);
    return klines;

  } catch (error) {
  logger.error('klines-error', error as any);
    throw new Error(`Failed to fetch klines for ${symbol}: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Convert Binance klines to lightweight-charts format
 * @param klines Array of Binance kline data
 * @returns Array in lightweight-charts candlestick format
 */
export function convertToLightweightChartsFormat(klines: BinanceKlineData[]): LightweightChartsKline[] {
  return klines.map(kline => ({
    time: Math.floor(kline.openTime / 1000), // Convert milliseconds to seconds
    open: parseFloat(kline.open),
    high: parseFloat(kline.high),
    low: parseFloat(kline.low),
    close: parseFloat(kline.close)
  }));
}

/**
 * Fetch klines and convert to lightweight-charts format in one step
 * @param symbol Trading pair symbol
 * @param interval Kline interval
 * @param limit Number of klines
 * @returns Promise with lightweight-charts compatible data
 */
export async function fetchLightweightChartsKlines(
  symbol: string,
  interval: string = '1m',
  limit: number = 100
): Promise<LightweightChartsKline[]> {
  const klines = await fetchBinanceKlines(symbol, interval, limit);
  return convertToLightweightChartsFormat(klines);
}

export default {
  fetchBinanceKlines,
  convertToLightweightChartsFormat,
  fetchLightweightChartsKlines,
  fetchAllTradingPairs
};

/**
 * Fetch all trading pairs from local backend API with 24hr statistics
 * This reduces direct calls to Binance API and uses cached data
 * @returns Promise with array of Asset objects
 */
export async function fetchAllTradingPairs(): Promise<Asset[]> {
  const logger = createLogger('binance:api');
  try {
    logger.debug('fetch-trading-pairs');

    const [exchangeInfo, ticker24hr] = await Promise.all([
      axios.get('/api/exchangeInfo', { timeout: 10000 }),
      axios.get('/api/24hr', { timeout: 10000 })
    ]);

    // Dozwolone rynki (quote) ‚Äî priorytet dla USDC (MiCA-compliant), backup USDT
    const MARKET_QUOTES: string[] = (((typeof process !== 'undefined' && (process as any).env?.VITE_MARKET_QUOTES) || 'USDC,USDT,BTC,ETH,BNB'))
      .split(',')
      .map((q: string) => q.trim().toUpperCase())
      .filter(Boolean);

    // Lista walut fiat dla par USDC{FIAT} i USDT{FIAT}
    const FIAT_CURRENCIES = new Set([
      'EUR', 'GBP', 'PLN', 'JPY', 'CNY', 'TRY', 'BRL', 'ARS', 'MXN', 'ZAR', 'UAH', 'RON',
      'KZT', 'NGN', 'CZK', 'CHF', 'SEK', 'NOK', 'DKK', 'HUF', 'AUD', 'NZD', 'CAD',
      'HKD', 'SGD', 'COP', 'CLP', 'PEN', 'PHP', 'IDR', 'INR', 'THB', 'VND', 'ILS',
      'AED', 'SAR', 'QAR', 'KRW', 'MYR'
    ]);

    // Filtruj aktywne pary: standardowe (quote w MARKET_QUOTES) + pary fiat (USDC{FIAT} i USDT{FIAT})
    const filteredPairs = (exchangeInfo.data as any).symbols.filter((symbol: any) => 
      symbol.status === 'TRADING' &&
      symbol.isSpotTradingAllowed &&
      (
        // Standardowe pary: quote asset w dozwolonych rynkach
        MARKET_QUOTES.includes(symbol.quoteAsset) ||
        // Pary fiat: USDC{FIAT} (MiCA-compliant, preferowane)
        (symbol.baseAsset === 'USDC' && FIAT_CURRENCIES.has(symbol.quoteAsset)) ||
        // Pary fiat: USDT{FIAT} (backup, bƒôdƒÖ usuniƒôte w EU)
        (symbol.baseAsset === 'USDT' && FIAT_CURRENCIES.has(symbol.quoteAsset))
      )
    );

    // Mapowanie danych na format Asset
  const assets: Asset[] = filteredPairs.map((pair: any) => {
      const tickerData = (ticker24hr.data as any[]).find((t: any) => t.symbol === pair.symbol);

      if (!tickerData) {
        // Fallback gdy brak ticker data
        return {
          symbol: pair.symbol,
          baseAsset: pair.baseAsset,
          quoteAsset: pair.quoteAsset,
          price: 0,
          priceChange: 0,
          priceChangePercent: 0,
          volume: 0,
          count: 0,
          status: pair.status,
        };
      }

      return {
        symbol: pair.symbol,
        baseAsset: pair.baseAsset,
        quoteAsset: pair.quoteAsset,
        price: parseFloat(tickerData.lastPrice),
        priceChange: parseFloat(tickerData.priceChange),
        priceChangePercent: parseFloat(tickerData.priceChangePercent),
        volume: parseFloat(tickerData.quoteVolume),
        count: tickerData.count,
        status: pair.status,
        highPrice: parseFloat(tickerData.highPrice),
        lowPrice: parseFloat(tickerData.lowPrice),
        openPrice: parseFloat(tickerData.openPrice),
        prevClosePrice: parseFloat(tickerData.prevClosePrice),
        weightedAvgPrice: parseFloat(tickerData.weightedAvgPrice),
        bidPrice: parseFloat(tickerData.bidPrice),
        askPrice: parseFloat(tickerData.askPrice),
        bidQty: parseFloat(tickerData.lastQty), // Using lastQty as approximation
        askQty: parseFloat(tickerData.lastQty),
      };
    });

    // Sortowanie po wolumenie (najwiƒôksze najpierw)
    const sortedAssets = assets.sort((a, b) => b.volume - a.volume);

  logger.debug('trading-pairs-fetched', { count: sortedAssets.length });
    return sortedAssets;

  } catch (error) {
  logger.error('trading-pairs-error', error as any);
    throw new Error(`Failed to fetch trading pairs: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

// Binance WebSocket Client for Kline/Candlestick streams
// Dedicated client for real-time candlestick data from Binance WebSocket API

export interface BinanceKlineData {
  e: string;      // Event type (always "kline")
  E: number;      // Event time (timestamp in milliseconds)
  s: string;      // Symbol (e.g., "BTCUSDT")
  k: {
    t: number;    // Kline start time (timestamp in milliseconds)
    T: number;    // Kline close time (timestamp in milliseconds)
    s: string;    // Symbol (e.g., "BTCUSDT")
    i: string;    // Interval (e.g., "1m")
    f: number;    // First trade ID
    L: number;    // Last trade ID
    o: string;    // Open price
    c: string;    // Close price
    h: string;    // High price
    l: string;    // Low price
    v: string;    // Base asset volume
    n: number;    // Number of trades
    x: boolean;   // Is this kline closed? (true when kline is finalized)
    q: string;    // Quote asset volume
    V: string;    // Taker buy base asset volume
    Q: string;    // Taker buy quote asset volume
  };
}

export type BinanceKlineListener = (data: BinanceKlineData) => void;

export class BinanceWSClient {
  private ws: WebSocket | null = null;
  private listeners: BinanceKlineListener[] = [];
  private symbol: string;
  private interval: string;
  private shouldReconnect = true;
  private reconnectTimeout: number | null = null;
  private reconnectAttempts = 0;
  private maxReconnectAttempts = 5;
  private isDestroyed = false;

  constructor(symbol: string, interval: string = '1m') {
    this.symbol = symbol.toLowerCase();
    this.interval = interval;
    this.connect();
  }

  private connect() {
    if (this.isDestroyed) {
      console.log('[BinanceWSClient] Client is destroyed, skipping connect');
      return;
    }

  // Use environment variable for Binance WebSocket URL (tests use process.env)
  const baseUrl = (typeof process !== 'undefined' && (process as any).env?.VITE_BINANCE_WS_URL) || 'wss://data-stream.binance.vision/ws';
    const url = `${baseUrl}/${this.symbol}@kline_${this.interval}`;
    console.log(`[BinanceWSClient] Connecting to ${url}`);
    console.log(`[BinanceWSClient] Using ${baseUrl.includes('data-stream') ? 'data-stream.binance.vision (optimized for market data)' : 'stream.binance.com (full API)'}`);

    try {
      this.ws = new WebSocket(url);

      this.ws.onopen = () => {
        console.log(`[BinanceWSClient] Connected to ${url}`);
        this.reconnectAttempts = 0;
      };

      this.ws.onmessage = (event) => {
        try {
          const data: BinanceKlineData = JSON.parse(event.data);
          if (data.e === 'kline') {
            this.notifyListeners(data);
          }
        } catch (error) {
          console.error('[BinanceWSClient] Failed to parse message:', error);
        }
      };

      this.ws.onerror = (error) => {
        console.error('[BinanceWSClient] WebSocket error:', error);
        console.error('[BinanceWSClient] Connection URL:', url);
        console.error('[BinanceWSClient] WebSocket readyState:', this.ws?.readyState);

        // Additional error information
        if (this.ws?.readyState === WebSocket.CLOSING) {
          console.warn('[BinanceWSClient] WebSocket is closing');
        } else if (this.ws?.readyState === WebSocket.CLOSED) {
          console.warn('[BinanceWSClient] WebSocket is closed');
        }
      };

      this.ws.onclose = (event) => {
        console.log(`[BinanceWSClient] Connection closed: ${event.code} ${event.reason}`);
        this.ws = null;

        // Don't attempt reconnection if the close was due to an invalid endpoint (code 1006)
        if (event.code === 1006) {
          console.warn('[BinanceWSClient] Connection failed - possibly invalid endpoint or unsupported stream. Check if testnet supports kline streams.');
          console.warn('[BinanceWSClient] Consider setting VITE_ENABLE_BINANCE_STREAMS=false in development.');
          this.shouldReconnect = false;
          return;
        }

        if (this.shouldReconnect && !this.isDestroyed && this.reconnectAttempts < this.maxReconnectAttempts) {
          const delay = Math.min(1000 * Math.pow(2, this.reconnectAttempts), 30000);
          console.log(`[BinanceWSClient] Reconnecting in ${delay}ms (attempt ${this.reconnectAttempts + 1}/${this.maxReconnectAttempts})`);

          this.reconnectTimeout = window.setTimeout(() => {
            this.reconnectAttempts++;
            this.connect();
          }, delay);
        } else if (this.reconnectAttempts >= this.maxReconnectAttempts) {
          console.error('[BinanceWSClient] Max reconnection attempts reached. Giving up.');
        }
      };

    } catch (error) {
      console.error('[BinanceWSClient] Failed to create WebSocket:', error);
    }
  }

  private notifyListeners(data: BinanceKlineData) {
    this.listeners.forEach(listener => {
      try {
        listener(data);
      } catch (error) {
        console.error('[BinanceWSClient] Error in listener:', error);
      }
    });
  }

  addListener(listener: BinanceKlineListener) {
    this.listeners.push(listener);
    console.log(`[BinanceWSClient] Added listener, total: ${this.listeners.length}`);
  }

  removeListener(listener: BinanceKlineListener) {
    const index = this.listeners.indexOf(listener);
    if (index !== -1) {
      this.listeners.splice(index, 1);
      console.log(`[BinanceWSClient] Removed listener, total: ${this.listeners.length}`);
    }
  }

  destroy() {
    console.log('[BinanceWSClient] Destroying client');
    this.isDestroyed = true;
    this.shouldReconnect = false;

    if (this.reconnectTimeout) {
      clearTimeout(this.reconnectTimeout);
      this.reconnectTimeout = null;
    }

    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }

    this.listeners = [];
  }

  // Get current connection state
  get isConnected(): boolean {
    return this.ws?.readyState === WebSocket.OPEN;
  }

  // Change symbol (creates new connection)
  changeSymbol(newSymbol: string, newInterval?: string) {
    this.symbol = newSymbol.toLowerCase();
    if (newInterval) {
      this.interval = newInterval;
    }

    console.log(`[BinanceWSClient] Changing to ${this.symbol}@kline_${this.interval}`);

    // Close current connection and create new one
    if (this.ws) {
      this.ws.close();
    }

    this.reconnectAttempts = 0;
    this.connect();
  }
}

export default BinanceWSClient;

// getEnvVar.ts - Helper for environment variables in Vite

// Whitelist dozwolonych kluczy ≈õrodowiskowych dla bezpiecze≈Ñstwa
const ALLOWED_ENV_KEYS = [
  'VITE_API_URL',
  'VITE_WS_URL', 
  'VITE_API_KEY',
  'VITE_SECRET_KEY',
  'VITE_AUTH_TOKEN',
  'MODE',
  'API_URL',
  'WS_URL'
] as const;

type AllowedEnvKey = typeof ALLOWED_ENV_KEYS[number];

export function getEnvVar(key: AllowedEnvKey, fallback?: string): string {
  // Sprawd≈∫ czy klucz jest na whitelist
  if (!ALLOWED_ENV_KEYS.includes(key)) {
    console.warn(`Environment key '${key}' is not in allowed list`);
    return fallback ?? '';
  }

  // Use Vite's import.meta.env instead of process.env
  // Try process.env first (tests), then fallback
  const envValue = (typeof process !== 'undefined' ? (process as any).env?.[key] : undefined) as string | undefined;
  return envValue ?? fallback ?? '';
}

// getViteEnvVar.ts - tylko import.meta.env, do u≈ºycia w kodzie uruchamianym przez Vite

// Whitelist dozwolonych kluczy ≈õrodowiskowych dla bezpiecze≈Ñstwa
